import React from "react";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import { Form, Formik } from "formik";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_products";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import { useState } from "react";
import { useEffect } from "react";
import {
  getAllBrandsService,
  getAllColorService,
  getCategoriesService,
} from "../../../../services/CRUD_categoryService";
import { useLocation } from "react-router-dom";

export const AddProduct = () => {
  const [mainCategories, setMainCategories] = useState([]); // نگهداری همه محصولات والد
  const [colors, setColors] = useState([]);
  const [brands, setBrands] = useState([]);
  const [reInitialize, setReInitialize] = useState([]);
  const location = useLocation();

  // useEffect(() => {
  //   console.log(location);
  //   console.log(location.state.rowData);
  //   setReInitialize(location.state.rowData);
  // }, [location]);
  //=========== 👇 گرفتن همه محصولات والد =============
  const handleGetCategories = async () => {
    const res = await getCategoriesService();
    if (res.status == 200) {
      if (typeof res.data.data == "object") {
        for (const key in res.data.data) {
          setMainCategories((oldData) => [
            ...oldData,
            { id: res.data.data[key].id, value: res.data.data[key].title },
          ]);
        }
      } else {
        console.log("its not object");
      }
    }
  };

  useEffect(() => {
    handleGetCategories();
  }, []);
  //============ 👆 گرفتن همه محصولات والد =============
  //=============== 👇 گرفتن رنگ ها ===================
  const handleGetColors = async () => {
    const res = await getAllColorService();
    if (res.status == 200) {
      if (typeof res.data.data == "object") {
        for (const key in res.data.data) {
          setColors((oldData) => [
            ...oldData,
            { id: res.data.data[key].id, value: res.data.data[key].title },
          ]);
        }
      } else {
        console.log("its not object");
      }
    }
  };
  useEffect(() => {
    handleGetColors();
  }, []);
  //=============== 👆 گرفتن رنگ ها ===================
  // ============== 👇 گرفتن برند ها ===================
  const handleGetBrands = async () => {
    const res = await getAllBrandsService();
    if (res.status == 200) {
      if (typeof res.data.data == "object") {
        setBrands()
      } else {
        console.log("its not object");
      }
    }
  };
  useEffect(() => {
    handleGetBrands();
  }, []);
  // ============== 👆 گرفتن برند ها ===================
  return (
    <div className="">
      {/* ================= 📍 header of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container">
        <h5>
          {location.state.title_for_adding
            ? location.state.title_for_adding
            : location.state.title_for_editing}
        </h5>
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>

      {/* =================== 📍 start form  ======================= */}
      <div className="container col-lg-5 ">
        <Formik
          initialValues={reInitialize || initialValues}
          onSubmit={(rowData) => onSubmit(rowData)}
          validationSchema={validationSchema}
          enableReinitialize
        >
          {(form) => {
            return (
              <Form>
                {mainCategories.length ? (
                  <FormikControl
                    control="select"
                    label="دسته"
                    option={mainCategories}
                    name="category_ids"
                  />
                ) : (
                  "دسته والد"
                )}
                <FormikControl
                  control="input"
                  type="text"
                  name="title"
                  placeholder="عنوان"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="price"
                  title="قیمت"
                  placeholder="قیمت (تومان)"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="weight"
                  title="وزن"
                  placeholder="وزن (کیلوگرم)"
                />
                {/* <FormikControl
                  control="select"
                  label="برند"
                  option={}
                  name="brand_id"
                /> */}
                {colors.length ? (
                  <FormikControl
                    control="select"
                    label="رنگ"
                    option={colors}
                    name="color_ids"
                  />
                ) : (
                  "colors"
                )}
                {/* <FormikControl
                  control="select"
                  label="گارانتی"
                  option={}
                  name="guarantee_ids"
                />  */}
                <FormikControl
                  control="textArea"
                  type="type"
                  name="descriptions"
                  placeholder="توضیحات"
                />
                <FormikControl
                  control="addFile"
                  formik={form}
                  label="تصویر"
                  name="image"
                  placeholder="📂 برای انتخاب تصویر اینجا کلیک کنید"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="alt_image"
                  placeholder="توضیح تصویر"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="keywords"
                  placeholder=" تگ ها"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="stock"
                  title="موجود"
                  placeholder="موجودی (تعداد)"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="discount"
                  title="تخفیف"
                  placeholder="درصد تخفیف"
                />
                <div className=" text-center pt-4">
                  <SubmitBtn />
                </div>
              </Form>
            );
          }}
        </Formik>
      </div>
    </div>
  );
};
