import { FastField } from "formik";
import React from "react";
import { DatePicker } from "react-advance-jalaali-datepicker";

export const PersianDate = ({ label, type, name }) => {
  return (
    <div>
      <div className="input-group p-2">
        <span className="input-group-text titleSpan customStyle">{label}</span>
        <FastField type={type} name={name} className="form-control">
          {() => {
            return (
              <div className="form-control testStyle bg-danger">
                <DatePicker
                  className="form-control form-control-solid w-250px "
                  // selected={startDate}
                  // onChange={onChange}
                  selectsRange
                  // startDate={startDate}
                  // endDate={endDate}
                  isClearable={true}
                />
              </div>
            );
          }}
        </FastField>
      </div>
    </div>
  );
};
