import { Field } from "formik";
import React from "react";
import { Chips } from "../../general_compo/spinners&chips/Chips";
import { handleOnChange } from "../../pages/2-product/add_or_edit_product/Get_Items_dropdowns";

control = "multiSelect";
label = "دسته والد";
option = { parentsCategories };
name = "parent_id";
stateOfData = { setIdOfParent };

export const MultiSelect_Input = (props) => {
  const {
    chips,
    setChips,
    mainCategories,
    form,
    label,
    option,
    name,
    stateOfData,
  } = props;
  return (
    <div>
      <div className="p-2">
        <div className="input-group mb-1">
          <span className="input-group-text w_6rem customStyle">دسته اصلی</span>
          <Field
            as="select"
            className="form-control form-select"
            name="main_ids"
            onChange={(e) => {
              handleOnChange(e, form, mainCategories, setChips);
            }}
          >
            <option hidden value>
              --
            </option>

            {mainCategories?.map((d) => (
              <option value={d.id} key={Math.random()}>
                {d.value}
              </option>
            ))}
          </Field>
        </div>
      </div>
      <Chips chips={chips} setChips={setChips} />
    </div>
  );
};
