import React, { useState } from "react";
import { useEffect } from "react";
import moment from "jalali-moment";

export const ConvertData = ({ data }) => {
  const [persionDate, setPersionDate] = useState();
  useEffect(() => {
    const todayJalali = moment(data.created_at).locale("fa").format("YYYY/M/D");
    setPersionDate([todayJalali]);
   // console.log(persionDate);
  }, [persionDate]);

  return (
    <div>
      {/* {persionDate.map((date) => (
