import axios from "axios";
import { FastField } from "formik";
import React from "react";
import { useState } from "react";

export const SwitchRememberMe = ({ label, name }) => {
  const [modifySwitch, setModifySwitch] = useState(0); // 0=off * 1=on

  const remember = () => {
    console.log("test remember me");

    const loginToken = JSON.parse(localStorage.getItem('loginToken'))

    if (modifySwitch == 0) {
      setModifySwitch(1);
      axios
        .get("http://authservice.azhadev.ir/api/auth/user" , {header:{ 'Authorization' : `Bearer ${loginToken}`}})
        .then((res) => {
          console.log("res : " + res);
        })
        .catch(() => {
          console.log("error catch");
        });
    } else {
      setModifySwitch(0);
    }
    console.log(modifySwitch);
  };
  return (
    <div onClick={remember} className="form-check form-switch pt-4">
      <FastField
        className="form-check-input pointer"
        type="checkbox"
        name={name}
      />
      <label className="form-check-label">{label}</label>
    </div>
  );
};

