import React, { useState } from "react";
import { UpperPartPages } from "../../general_compo/reusable_operations/UpperPartPages";
import { useEffect } from "react";
import { getAllDiscountsService } from "../../../services/CRUD_categoryService";
import { Reusable_table } from "../../general_compo/reusable_table/reusable_table1/Reusable_table";
import moment from "jalali-moment";
import { Operation_discounts } from "./Operation_discounts";
import { header_discountsTable } from "./FormikHelper_discounts";
import { AddNewDiscounts_modal } from "./AddNewDiscounts_modal";
import 'animate.css';
export const DiscountManagement = () => {
  const [data, setData] = useState([]);

  //============= 👇گرفتن همه تخفیف ها 📍 =================
  const handleGetAllDiscounts = async () => {
    const res = await getAllDiscountsService();
    if (res.status == 200) {
      console.log(res.data.data);
      setData(res.data.data);
    }
  };

  useEffect(() => {
    handleGetAllDiscounts();
  }, []);
  //==============  📍additionField =========================
  const additionField = [
    {
      title: "تاریخ انقضا",
      elements: (data) =>
        moment(data.created_at).locale("fa").format("YYYY/M/D"),
    },
    {
      title: "عملیات",
      elements: (rowData) => (
        <Operation_discounts rowData={rowData} data={data} setData={setData} />
      ),
    },
  ];

  //======================= 👇return ==========================
  return (
    <>
      <div className="px-4 productGroupManagement">
        <UpperPartPages title="مدیریت تخفیف ها" />
        <button className="btn btn-info animate__flash">dd</button>
        <h1 class="animate__animated animate__bounce">An animated element</h1>
        <div className="pt-5">
          <Reusable_table
            nameOfColumn={header_discountsTable}
            dataOfRows={data}
            placeholder_searchBox="قسمتی از عنوان را وارد کنید"
            additionField={additionField}
            having_searchBox={true}
            modal_addButton={true}
            modal_compo={<AddNewDiscounts_modal />}
            
          />
        </div>
      </div>
    </>
  );
};
