import { Formik, Form } from "formik";
import React from "react";
import { FormikControl } from "./FormikControl_AddItems";
import { useEffect } from "react";
import { useState } from "react";
import {
  handleGetParentsCategories,
  initialValues,
  onSubmit,
  validationSchema,
} from "./FormikAtt_AddItems";
//====================================
export const FormikAddItems = () => {
  const [parents, setParents] = useState([]);

  useEffect(() => {
    handleGetParentsCategories(setParents);
  }, []);

  return (
    <Formik
      initialValues={initialValues}
      onSubmit={(values) => onSubmit(values)}
      validationSchema={validationSchema}
    >
      {(formik) => {
        //console.log(formik);
        return (
          <Form className="">
            {parents.length > 0 ? (
              <FormikControl
                control="select"
                type="select"
                name="parent_id"
                label="دسته والد"
                option={parents}
              />
            ) : null}

            <FormikControl
              formik={formik}
              control="input"
              type="text"
              name="title"
              placeholder="عنوان"
              inputStyle="registerInputStyle"
            />
            <FormikControl
              control="textArea"
              type="textarea"
              name="description"
              placeholder="توضیحات"
            />
            <FormikControl
              control="addFile"
              type="text"
              name="addFile"
              placeholder="برای انتخاب تصویر خود کلیک کنید."
            />
            <div className="d-flex justify-content-center align-items-center">
              <FormikControl
                control="checkbox"
                name="is_active"
                label="وضعیت فعال"
              />

              <FormikControl
                control="checkbox"
                name="show_in_menu"
                label="نمایش در منو"
              />
            </div>
            <div >
              <button type="submit" className="btn btn-success">
                ذخیره
              </button>
            </div>
          </Form>
        );
      }}
    </Formik>
  );
};
