import React, { useState } from "react";
import { AddItem_btn } from "../../general_compo/add_or_edit_product/AddItem_btn";
import { Form, Formik } from "formik";
import { FormikControl } from "../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../formik/SubmitBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "./FormikHelper_discounts";
import { getAllTitlesOfProducts } from "../../../services/CRUD_categoryService";
import { useEffect } from "react";
import { NavLink } from "react-router-dom";
import "animate.css";

export const AddNewDiscounts_modal = ({ editDiscount, rowData }) => {
  const [titlesOfProducts, setTitlesOfProducts] = useState([]);
  const [chips_productTitles, setChips_productTitles] = useState([]);
  const [reInitialize, setReInitialize] = useState([]);
  const [reInitialize2, setReInitialize2] = useState([]);

  useEffect(() => {
    console.log(reInitialize);
    setReInitialize2({
      id: " data.id",
      title: rowData.title,
      code: rowData.code,
      percent: rowData.percent,
    });
  }, [reInitialize]);

  const handleGetProduct_ids = async () => {
    const res = await getAllTitlesOfProducts();
    if (res.status == 200) {
      setTitlesOfProducts(
        res.data.data.map((d) => {
          return {
            id: d.id,
            value: d.title,
          };
        })
      );
    }
  };

  useEffect(() => {
    handleGetProduct_ids();
  }, []);

  //======================================================
  const handleSetProductSelectBox = (formik) => {
    return (
      <FormikControl
        control="multiSelect"
        label="برای"
        option={titlesOfProducts}
        name="product_ids"
        chips={chips_productTitles}
        setChips={setChips_productTitles}
        addBtnOption={true}
        customAnimatedClass="animate__animated animate__headShake"
        // addBtnPath="/colour-management"
        // selectedItems={selectedTitlesOfProd}
      />
    );
  };
  //===================== 📍 handle reset form ==========================
  const handleResetForm = () => {
    document.getElementById("formOfAddDiscounts").reset();
  };

  //====================== 📍 start main return =========================
  return (
    <div>
      {/* <!-- Button trigger modal ➕icon --> */}
      <span data-bs-toggle="modal" data-bs-target="#staticBackdrop">
        {/* <NavLink to={"/discount-management/add-edit-discount"}> */}
        {editDiscount ? (
          <i
            onClick={() => {
              console.log(rowData);
              // setReInitialize([rowData]);
              setReInitialize({
                id: " data.id",
                title: rowData.title,
                code: rowData.code,
                percent: rowData.percent,
                is_active: "",
                for_all: true,
                expire_at: rowData.expire_at,
                product_ids: rowData.products?.map((p) => p.title),
              });
            }}
            className="icon_product_table fas fa-edit text-warning mx-1 pointer has_tooltip"
          ></i>
        ) : (
          <AddItem_btn />
        )}
        {/* </NavLink> */}
      </span>

      {/* <!-- Modal --> */}
      <div
        className="modal fade"
        id="staticBackdrop"
        data-bs-backdrop="static"
        data-bs-keyboard="false"
        tabindex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content animate__animated animate__pulse">
            <div className="modal-header d-flex justify-content-between align-items-center">
              <h5 className="modal-title w-100 mx-5" id="staticBackdropLabel">
                افزودن کد تخفیف
              </h5>
              <NavLink to={"/discount-management"} className="ps-4">
                <button
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="modal"
                  aria-label="Close"
                  onClick={() => handleResetForm()}
                ></button>
              </NavLink>
            </div>

            <div className="modal-body">
              <Formik
                initialValues={reInitialize2}
                onSubmit={(values, action) => onSubmit(values, action)}
                validationSchema={validationSchema}
                enableReinitialize
              >
                {(formik) => {
                  // console.log(formik.values);
                  return (
                    <Form id="formOfAddDiscounts">
                      <FormikControl
                        control="input"
                        type="text"
                        name="title"
                        placeholder="عنوان"
                      />
                      <FormikControl
                        control="input"
                        type="text"
                        name="code"
                        placeholder="کد تخفیف"
                      />
                      <FormikControl
                        control="input"
                        type="number"
                        name="percent"
                        placeholder="درصد تخفیف"
                      />

                      <FormikControl
                        control="persianDate"
                        formik={formik}
                        name="expire_at"
                        label="تاریخ انقضاء"
                        yearsLimit={{ from: 10, to: 10 }}
                      />

                      <FormikControl
                        control="checkbox"
                        name="for_all"
                        label="برای همه"
                        customClass="text-center"
                      />

                      {!formik.values.for_all
                        ? handleSetProductSelectBox(formik)
                        : null}
                      <div className="text-center">
                        <SubmitBtn />
                      </div>
                    </Form>
                  );
                }}
              </Formik>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
