import axios from "axios";
import { FastField } from "formik";
import React from "react";
import { useEffect } from "react";

export const SwitchRememberMe = ({ label, name }) => {
  useEffect(() => {
    const loginToken = JSON.parse(localStorage.getItem("loginToken"));
    console.log(loginToken);
    axios.get("http://authservice.azhadev.ir/api/auth/user", {
      header: {
        Authorization: `Bearer ${loginToken.token}`,
      },
    });
  }, []);

  const [token, setToken] = useState(
    sessionStorage.getItem('token') || localStorage.getItem('loginToken'),
  );
  
  const login = (token: string, rememberMe = false) => {
    if (rememberMe) {
      localStorage.setItem('token', token);
    } else {
      sessionStorage.setItem('token', token);
    }
    setToken(token);
  };
  
  const logout = () => {
    localStorage.removeItem('token');
    sessionStorage.removeItem('token');
    setToken(null);
  };
  return (
    <div className="form-check form-switch pt-4">
      <FastField
        className="form-check-input pointer"
        type="checkbox"
        name={name}
      />
      <label className="form-check-label">{label}</label>
    </div>
  );
};

