import { useEffect, useState } from "react";
import { NavLink, useLocation } from "react-router-dom";
import { PrevPageBtn } from "../reusable_operations/PrevPageBtn";
import { Pagination } from "./Pagination";
import { SearchBox } from "../reusable_operations/SearchBox";
import { AddItem_btn } from "../add_or_edit_product/AddItem_btn";
import { AddNewBrands } from "../../pages/brand/add_or_edir_brands/AddNewBrands";
import { Table } from "./Table";
import { BrandsManagement } from "../../pages/brand/BrandsManagement";
import { Formik } from "formik";
import { initialValues } from "../../pages/brand/FormikHelper_brands";

const numOfRows_singlePage = 5; //ÿØÿ± Ÿáÿ± ÿµŸÅÿ≠Ÿá ⁄ÜŸÜÿØ ÿ±ÿØ€åŸÅ ÿßÿ≤ ÿ≠ÿØŸàŸÑ ŸÜŸÖÿß€åÿ¥ ÿØÿßÿØŸá ÿ¥ŸàÿØ

export const Reusable_table = (props) => {
  const location = useLocation();
  //üèÆüëâ ÿßÿ¥onClick :ÿßÿ≤ ÿß€åŸÜ ŸáŸà⁄© ÿß€åŸÜÿ¨ÿß ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿ¥ÿØŸá in Operations_product

  const {
    nameOfColumn, //ÿ™€åÿ™ÿ±Ÿáÿß
    dataOfRows,
    placeholder_searchBox,
    go_where, //this icon ‚ûï link to where - hamishe ham link nist
    show_addButton,
    modal,
    show_compo,
    show_subGroup,
    having_searchBox,
    additionField,
  } = props;

  const [dataAnyPage, setDataAnyPage] = useState([]); //slice shodeye dataOfRows
  const [currentPage, setCurrentPage] = useState(1);
  const [countPage, setCountPage] = useState(2); //page_count = dataOfRows.length / numOfRows_singlePage (ÿ™ÿπÿØÿßÿØ ⁄©ŸÑ ÿµŸÅÿ≠ÿßÿ™)
  const [pageArr, setPageArr] = useState([]); //[0,1,2,3,4,...,countPage]
  const [searchData, setSearchData] = useState(dataAnyPage);
  const [searchChar, setSearchChar] = useState("");

  //======== ŸÅ€åŸÑÿ™ÿ± ⁄©ÿ±ÿØŸÜ ÿ¢€åÿ™ŸÖ Ÿáÿß€å€å ⁄©Ÿá ŸÜŸÖÿß€åÿ¥ ÿØÿßÿØŸá ŸÖ€å ÿ¥ŸàÿØ ÿ®ÿ±ÿ≠ÿ≥ÿ® ÿ≥ÿ±⁄Ü ===========
  useEffect(() => {
    dataOfRows[0]?.title
      ? setSearchData(dataOfRows.filter((d) => d.title?.includes(searchChar)))
      : dataOfRows[0]?.original_name
      ? setSearchData(
          dataOfRows.filter((d) => d.original_name?.includes(searchChar))
        )
      : console.log("hint: check name of title");
  }, [searchChar, dataOfRows]);
  //==================   ÿ™ŸÇÿ≥€åŸÖ ⁄©ÿ±ÿØŸÜ ÿ±ÿØ€åŸÅ Ÿáÿß ÿ®€åŸÜ ÿµŸÅÿ≠ÿßÿ™   ======================
  useEffect(() => {
    //in useEffect , row ha ro be tedad page ha slice mikone
    const start = currentPage * numOfRows_singlePage - numOfRows_singlePage;
    const end = currentPage * numOfRows_singlePage;
    setDataAnyPage(searchData.slice(start, end));
  }, [currentPage, searchData]);

  //==========================================================================

  useEffect(() => {
    // Math.ceil() ==> round to up number
    const page_count = Math.ceil(searchData.length / numOfRows_singlePage);
    setCountPage(page_count);
    // ========================= //
    let arrOfPages = [];
    for (let i = 1; i <= page_count; i++) arrOfPages = [...arrOfPages, i]; //[0,1,2,3,4,...,dataOfRows.length]
    setPageArr(arrOfPages);
    setCurrentPage(1);
  }, [searchData]);

  //===============================  return ==================================

  return (
    <div className="p-1 reusable_table">
      {show_subGroup ? (
        location.state ? (
          <h5 className="text-center d-flex justify-content-center bg-danger">
            ÿ≤€åÿ± ⁄Øÿ±ŸàŸá:
            <PrevPageBtn returnTitle={location.state.parentData?.title} />
          </h5>
        ) : null
      ) : null}

      <div className="d-flex justify-content-between align-items-center p-2">
        {/* ================== üîç start searchBoxüëá ================== */}
        {having_searchBox ? (
          <SearchBox
            setSearchChar={setSearchChar}
            placeholder_searchBox={placeholder_searchBox}
          />
        ) : null}

        {/* ================== üîç end searchBoxüëÜ ==================== */}

        {/* ================== ‚ûï start show_addButtonüëá ================== */}
        {/* in icon ‚ûï  */}
        {show_addButton ? (
          <NavLink to={go_where}>
            <AddItem_btn show_compo={show_compo} />
          </NavLink>
        ) : null}
        {/* ============================ */}

        <div className="bg-danger">
          <button
            type="button"
            className="btn btn-success d-flex justify-content-center align-items-center"
            data-bs-toggle="modal"
            data-bs-target="#staticBackdrop"
          >
            <i className="fas fa-plus text-light"></i>
          </button>

          {/* ================= üìçstart modal ================= */}
          <TestModal
            Formik={Formik}
            initialValues={initialValues}
            onSubmit={onSubmit}
            validationSchema={validationSchema}
            Form={Form}
            FormikControl={FormikControl}
            SubmitBtn={SubmitBtn}
          />
          {/* ================= üìçend modal ================= */}
        </div>
        {/* ============================ */}
      </div>
      {/* ================== ‚ûï end show_addButtonüëÜ ==================== */}

      {/* ================== start tableüëá ====================== */}
      <Table
        dataOfRows={dataOfRows}
        nameOfColumn={nameOfColumn}
        additionField={additionField}
        dataAnyPage={dataAnyPage}
      />
      {/* ================== end tableüëÜ ======================== */}

      {/* ================== start paginationüëá ================= */}
      <Pagination
        countPage={countPage}
        currentPage={currentPage}
        setCountPage={setCountPage}
        pageArr={pageArr}
        setCurrentPage={setCurrentPage}
      />
      {/* ================== end paginationüëÜ =================== */}
    </div>
  );
};
