import * as Yup from "yup";
import { createNewColorService } from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

//====================== üìçinitialValues =====================
export const initialValues = {
  title: "",
  code: "#fffff",
};

//====================== üìçonSubmit ===========================
export const onSubmit = async (
  values,
  actions,
  setData,

) => {
  console.log(values);
  const res = await createNewColorService(values);
  if (res.status == 201) {
    swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!...", res.data.message, "success");
    setData((oldData) => [...oldData, res.data.data]);
    actions.resetForm();
    console.log(actions);
    actions.setFieldValue("code", "#fffff");
  }
};

//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({});
