import { FastField, Form, Formik } from "formik";
import React from "react";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_colour";
import { useState } from "react";

export const AddNewColor = ({ colorToEdit, data, setData }) => {
  const [newData, setNewData] = useState(null);
  return (
    <div className="modal-content">
      <Formik
        initialValues={initialValues}
        onSubmit={() => onSubmit(values, setData)}
        validationSchema={validationSchema}
      >
        {(formik) => {
          console.log(formik.values);
          setNewData(formik.values);
          return (
            <Form>
              {/* ==============  modal-header ============= */}
              <div className="modal-header">
                <h1 className=" w-100 fs-5" id="staticBackdropLabel">
                  اضافه کردن گارانتی جدید
                </h1>
              </div>
              {/* ==============  modal-body ============== */}
              <FormikControl
                control="input"
                type="text"
                name="title"
                placeholder="رنگ"
              />
              <FastField>
                {() => {
                  return (
                    <div className="d-flex align-items-center justify-content-start m-3">
                      <label htmlFor="code_color" className="ps-3">
                        کد رنگ
                      </label>
                      <input name="code" type="color" id="code_color" />
                    </div>
                  );
                }}
              </FastField>
              <div className="text-center  pb-3">
                <SubmitBtn />
              </div>
            </Form>
          );
        }}
      </Formik>
    </div>
  );
};
