import React from "react";
import { useState } from "react";
import { getProductsService } from "../../../../services/CRUD_categoryService";
import { useEffect } from "react";
import { UpperPartPages } from "../../reusable_operations/UpperPartPages";
import { NavLink } from "react-router-dom";
import { AddItem_btn } from "../../add_or_edit_product/AddItem_btn";
import { Operation_products } from "../../../pages/2-product/table_addition/Operation_products";
import { Pagination } from "react-bootstrap";

export const Reusable_table2 = () => {
  const [data, setData] = useState([]);
  const [currentPage, setCountPage] = useState(1); //صفحه فعلی
  const [countOfProducts_everyPage, setCountOfProducts_everyPage] = useState(5); // تعداد محصولات که در هر صفحه نمایش داده میشود
  const [pageCount, setPageCount] = useState([]); //تعداد کل صفحات
  const [listOfPages, setListOfPages] = useState([]); //یک ارایه از تعداد صفحات
  const [charSearch, setSearchChar] = useState("");

  //============== هدرهای جدول📍====================
  const headers_productTable = [
    { field: "id", title: "#" },
    {
      field: null,
      title: "گروه محصول",
      elements: (rowData) =>
        rowData.categories[0] ? rowData.categories[0].title : "ندارد",
    },
    { field: "title", title: "عنوان" },
    { field: "price", title: "قیمت" },
    { field: "stock", title: "موجودی" },
    {
      field: null,
      title: "عملیات",
      elements: (rowData) => (
        <Operation_products rowData={rowData} data={data} setData={setData} />
      ),
    },
  ];
  //============== 📍 گرفتن محصولات ===================
  const handleGetProducts = async () => {
    const res = await getProductsService(
      currentPage,
      countOfProducts_everyPage,
      charSearch
    );
    //console.log(res);
    if (res.status == 200) {
      setData(res.data.data);
      setPageCount(res.data.last_page);
      console.log(res.data.last_page);
    }
  };
  useEffect(() => {
    handleGetProducts();
  }, []);

  //============== 📍 هندل سرچ باکس ===================

  //===================================================
  useEffect(() => {
    let pArr = [];
    for (let i = 1; i <= pageCount; i++) pArr.push(i);
    setListOfPages(pArr);
  }, [pageCount]);

  return (
    <div className="p-4 reusable_table">
      <UpperPartPages title="مدیریت محصول" />
      {/* =========== 📍 start paginated table =============== */}

      <div className="d-flex justify-content-between align-items-center p-2">
        {/* ================== 🔍 start searchBox👇 ================== */}
        <div className="input-group mb-3 searchBox">
          <span className="input-group-text searchButton " id="basic-addon2">
            جستجو
          </span>
          <input
            type="text"
            className="form-control searchInput"
            placeholder="عنوان را جستجو کنید"
          />
        </div>

        {/* ================== 🔍 end searchBox👆 ==================== */}

        {/* ================== ➕ start show_addButton👇 ================== */}
        {/* in icon ➕  */}

        <NavLink>
          <AddItem_btn />
        </NavLink>

        {/* ================== ➕ end show_addButton👆 ==================== */}
      </div>

      <table className=" table_of_reusable_table table table-striped table-responsive text-center table-hover table-bordered">
        <thead>
          <tr>
            {headers_productTable.map((header) => (
              <th>{header.title}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((rowData) => (
            <tr>
              {headers_productTable.map((header) =>
                header.field ? (
                  <td>{rowData[header.field]}</td>
                ) : (
                  <td>{header.elements(rowData)}</td>
                )
              )}
            </tr>
          ))}
        </tbody>
      </table>
      {/* ================= 📍start pagination ================== */}
      <div className="d-flex justify-content-center ">
        {listOfPages.map((pageNum)=>(
          <>{}</>
        ))}
        <nav aria-label="Page navigation example">
          <ul class="pagination">
            <li class="page-item">
              <a class="page-link" href="#" aria-label="Previous">
                <span aria-hidden="true">&laquo;</span>
              </a>
            </li>
            <li class="page-item">
              <a class="page-link" href="#">
                1
              </a>
            </li>
            <li class="page-item">
              <a class="page-link" href="#">
                2
              </a>
            </li>
            <li class="page-item">
              <a class="page-link" href="#">
                3
              </a>
            </li>
            <li class="page-item">
              <a class="page-link" href="#" aria-label="Next">
                <span aria-hidden="true">&raquo;</span>
              </a>
            </li>
          </ul>
        </nav>
      </div>
      {/* ================= 📍end pagination ================== */}
    </div>
  );
};
