import React from "react";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import { getAttributesService } from "../../../../services/CRUD_categoryService";
import { useState } from "react";
import { Form, Formik } from "formik";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";

export const AddAttributes = () => {
  const location = useLocation();
  const [typeAtt, setTypeAtt] = useState([]);

  // useEffect(() => {
  //   console.log(location.state.rowData);
  //   console.log(location.state.rowData.categories[0].id);
  // }, [location]);
  //============= گرفتن تایپ ویژگی ها از محصول والد =============
  const handleGetTypeOfAtt = async () => {
    const res = await getAttributesService(
      location.state.rowData.categories[0].id
    );
    if (res.status == 200) {
      setTypeAtt(res.data.data);
      // console.log(res.data.data);
    }
  };
  useEffect(() => {
    handleGetTypeOfAtt();
  }, []);

  return (
    <div className=" container mt-5">
      <div>
        <div className=" text-center fs-3 p-3 fw-bold">افزودن ویژگی جدید :</div>
        <PrevPageBtn returnTitle/>
        <Formik>
          {(formik) => {
            return (
              <div className="">
                <Form>
                  <FormikControl control="typeOfAtt" name="typeOfAtt" />
                </Form>
              </div>
            );
          }}
        </Formik>
      </div>
    </div>
  );
};
