import { FastField } from "formik";
import React from "react";
import { useState } from "react";

export const SwitchRememberMe = ({ label, name }) => {
  const [token, setToken] = useState(
    sessionStorage.getItem("loginToken") || localStorage.getItem("loginToken")
  );

  const login = (token: string, rememberMe = false) => {
    if (rememberMe) {
      localStorage.setItem("loginToken", token);
    } else {
      sessionStorage.setItem("loginToken", token);
    }
    setToken(token);
  };

  const logout = () => {
    localStorage.removeItem("loginToken");
    sessionStorage.removeItem("loginToken");
    setToken(null);
  };
  console.log('test remember');
  return (
    <div className="form-check form-switch pt-4">
      <FastField
        className="form-check-input pointer"
        type="checkbox"
        name={name}
      />
      <label className="form-check-label">{label}</label>
    </div>
  );
};

