import { Form, Formik } from "formik";
import React, { useEffect, useState } from "react";
import { FormControl } from "../../pages/authorization/FormControl_Auth";
import { SubmitBtn } from "../../formik/SubmitBtn";
import { useLocation, useNavigate } from "react-router-dom";
import { getAttributesService } from "../../../services/CRUD_categoryService";
import { Reusable_table } from "../Reusable_table";
import { Operations_attributes } from "./Operations_attributes";
import {
  headers_attributesTable,
  initialValues,
  onSubmit,
  validationSchema,
} from "./FormikHelper_Attributes";

export const AddAttribute = () => {
  const location = useLocation(); //from Operations_product compo
  const [attData, setAttData] = useState([]);
  const [getAttToEdit, setGetAttToEdit] = useState(null);
  const navigate = useNavigate();

  //=====üìç⁄Øÿ±ŸÅÿ™ŸÜ Ÿà€å⁄ò⁄Ø€å Ÿáÿß€å €å⁄© ŸÖÿ≠ÿµŸàŸÑ ÿ®ÿ±ÿß€å ŸÜÿ¥ÿßŸÜÿØŸÜ ÿØÿ± ÿ¨ÿØŸàŸÑ ========
  const handleGetAttributes = async () => {
    const res = await getAttributesService(location.state?.categoryId);
    try {
      setAttData(res.data.data);
    } catch {}
  };

  useEffect(() => {
    handleGetAttributes();
  }, []);

  // ========== üìçadditionField for add attributes ==============
  const additionField = [
    {
      title: "ÿπŸÖŸÑ€åÿßÿ™",
      elements: (rowData) => (
        <Operations_attributes
          rowData={rowData}
          data={attData}
          setData={setAttData}
          getAttToEdit={getAttToEdit}
          setGetAttToEdit={setGetAttToEdit}
        />
      ),
    },
  ];

  //======================= return ===========================

  return (
    <div className="container">
      <div className="row justify-content-center">
        <Formik
          initialValues={getAttToEdit || initialValues} //ÿ™ÿ±ÿ™€åÿ®ÿ¥ ŸÖŸáŸÖŸá üòê
          enableReinitialize
          onSubmit={(values, form) =>
            onSubmit(values, form, location, setAttData, setReInitialValues)
          }
          validationSchema={validationSchema}
        >
          <Form>
            <div
              className="text-start"
              onClick={() =>
                navigate(
                  `/product-group-management/${location.state?.parentId}`
                )
              }
            >
              <i className="fa-solid fa-xmark fs-2 text-secondary"></i>
            </div>
            <div
              className={`text-center fs-4 ${getAttToEdit ? "d-none" : null}`}
            >
              ÿßŸÅÿ≤ŸàÿØŸÜ Ÿà€å⁄ò⁄Ø€å ÿ¨ÿØ€åÿØ
            </div>
            <div
              className={`row ${
                getAttToEdit
                  ? "edit_attribute_box"
                  : "cancel_edit_attribute_box"
              }`}
            >
              <div
                className={`fs-5 text-center ${getAttToEdit ? null : "d-none"}`}
              >
                Ÿà€åÿ±ÿß€åÿ¥ Ÿà€å⁄ò⁄Ø€å : {getAttToEdit?.title}
              </div>
              <div className=" col-md-6 col-lg-4 my-1">
                <FormControl
                  control="input"
                  type="text"
                  name="attributeTitle"
                  placeholder="ÿπŸÜŸàÿßŸÜ Ÿà€å⁄ò⁄Ø€å ÿ¨ÿØ€åÿØ"
                  errMs="ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ"
                />
              </div>

              <div className="col-md-6 col-lg-4 my-1">
                <FormControl
                  control="input"
                  type="text"
                  name="attributeUnit"
                  placeholder="Ÿàÿßÿ≠ÿØ Ÿà€å⁄ò⁄Ø€å ÿ¨ÿØ€åÿØ"
                  errMs="ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ"
                />
              </div>
              <div className="col-md-6 col-lg-3 my-1 d-flex justify-content-around">
                <FormControl
                  control="switch"
                  label="ŸÜŸÖÿß€åÿ¥ ÿØÿ± ŸÅ€åŸÑÿ™ÿ±"
                  name="switchShowFilter"
                />
                <div className="mt-3">
                  <SubmitBtn />
                  {getAttToEdit ? (
                    <button
                      className="btn btn-secondary"
                      onClick={() => setGetAttToEdit(null)}
                    >
                      ÿßŸÜÿµÿ±ÿßŸÅ
                    </button>
                  ) : null}
                </div>
              </div>
            </div>
          </Form>
        </Formik>
        <hr />
        <Reusable_table
          nameOfColumn={headers_attributesTable}
          dataOfRows={attData}
          having_searchBox={true}
          placeholder_searchBox="ŸÇÿ≥ŸÖÿ™€å ÿßÿ≤ ÿπŸÜŸàÿßŸÜ ÿ±ÿß Ÿàÿßÿ±ÿØ ⁄©ŸÜ€åÿØ"
          additionField={additionField}
          show_subset_icon={false}
        />
      </div>
    </div>
  );
};
