const handleGetTypeOfAtt = async () => {
  let attrVar = [];
  let initials = {};
  let rules = {};
  Promise.all(
    location.state.rowData.categories.map(async (cat) => {
      const res = await getAttributesService(cat.id);
      if (res.status == 200) {
        attrVar = [
          ...attrVar,
          { groupTitle: cat.title, data: res.data.data },
        ];
        if (res.data.data.length > 0) {
          for (const d of res.data.data) {
            initials = { ...initials, [d.id]: "" };
            rules = { ...rules, [d.id]: Yup.string() };
          }
        }
      }
    })
  ).then(() => {
    setTypeAtt(attrVar);
    setInitialValues(initials);
    setValidationSchema(
      Object.keys(initials).length > 0 ? Yup.object(rules) : {}
    );
  });
};

