import { Form, Formik } from "formik";
import React, { useEffect } from "react";
import { FormControl } from "../pages/authorization/FormControl_Auth";
import { SubmitBtn } from "../formik/SubmitBtn";
import { useLocation } from "react-router-dom";
import {
  createNewAttributeService,
  getAttributesService,
} from "../../services/CRUD_categoryService";

export const AddAttribute = () => {
  const location = useLocation();

  // useEffect(() => {
  //   console.log(location.state.categoryId);
  //   console.log(location.state);
  // }, [location]);

  const handleGetAttributes = async () => {
    const res = await getAttributesService();
    try {
      console.log("res");
    } catch {}
  };

  useEffect(() => {
    handleGetAttributes();
  }, []);

  const initialValues = {
    attributeTitle: "",
    attributeUnit: "",
  };

  const onSubmit = async (values) => {
    log
    const res = await createNewAttributeService(
      location.state.categoryId,
      values
    );
    try {
      console.log(res);
    } catch (error) {
      console.log("err");
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center ">
        <Formik
          initialValues={initialValues}
          onSubmit={(values) => onSubmit(values)}
        >
          <Form>
            <div className="row ">
              <div className=" col-md-6 col-lg-4 my-1">
                <FormControl
                  control="input"
                  type="text"
                  name="attributeTitle"
                  placeholder="عنوان ویژگی جدید"
                  errMs="لطفا این قسمت را پر کنید"
                />
              </div>
              <div className="col-md-6 col-lg-4 my-1">
                <FormControl
                  control="input"
                  type="text"
                  name="attributeUnit"
                  placeholder="واحد ویژگی جدید"
                  errMs="لطفا این قسمت را پر کنید"
                />
              </div>
              <div className="col-md-6 col-lg-4 my-1 d-flex justify-content-around">
                <FormControl
                  control="switch"
                  label="نمایش در فیلتر"
                  name="switchShowFilter"
                />
                <SubmitBtn />
              </div>
            </div>
          </Form>
        </Formik>
        <hr />
      </div>
    </div>
  );
};
