import { FastField } from "formik";
import React from "react";
import { Spinner } from "react-bootstrap";

export const SubmitBtn = ({ categoryId }) => {
  console.log(categoryId);
  return (
    <FastField>
      {({ form }) => {
        return (
          <>
            <button className="btn btn-success" disabled={form.isSubmitting}>
              {categoryId ? "ذخیره تغییرات" : "ذخیره"}

              {form.isSubmitting ? (
                <Spinner
                  className="me-3"
                  animation="border"
                  variant="light"
                  size="sm"
                />
              ) : null}
            </button>
          </>
        );
      }}
    </FastField>
  );
};
