import * as Yup from "yup";
import { createNewAttrForProductService } from "../../../../services/CRUD_categoryService";

//====================== üìçinitialValues =====================
export const initialValues = {};

//====================== üìçonSubmit ===========================
export const onSubmit = async (Id_product, value) => {
  console.log(Id_product);
  console.log(value);
  const res = await createNewAttrForProductService(Id_product, {
    1: {
      typeOfAtt_380: "test1",
    },
    2: {
      typeOfAtt_383: "test2",
    },
    3: {
      typeOfAtt_384: "tyyyyyyyyyyyyyyyyy",
    },
  });
  console.log(res);
};

//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({});
