import React from "react";
import { useState } from "react";
import { getProductsService } from "../../../../services/CRUD_categoryService";
import { useEffect } from "react";
import { UpperPartPages } from "../../reusable_operations/UpperPartPages";
import { NavLink } from "react-router-dom";
import { AddItem_btn } from "../../add_or_edit_product/AddItem_btn";
import { Pagination } from "./Pagination";
import { Table } from "./Table";

export const Reusable_table2 = ({ nameOfColumn, data, setData }) => {
  const [currentPage, setCurrentPage] = useState(1); //ÿµŸÅÿ≠Ÿá ŸÅÿπŸÑ€å
  const [countOfProducts_everyPage, setCountOfProducts_everyPage] = useState(5); // ÿ™ÿπÿØÿßÿØ ŸÖÿ≠ÿµŸàŸÑÿßÿ™ ⁄©Ÿá ÿØÿ± Ÿáÿ± ÿµŸÅÿ≠Ÿá ŸÜŸÖÿß€åÿ¥ ÿØÿßÿØŸá ŸÖ€åÿ¥ŸàÿØ
  const [pageCount, setPageCount] = useState([]); //ÿ™ÿπÿØÿßÿØ ⁄©ŸÑ ÿµŸÅÿ≠ÿßÿ™
  const [listOfPages, setListOfPages] = useState([]); //€å⁄© ÿßÿ±ÿß€åŸá ÿßÿ≤ ÿ™ÿπÿØÿßÿØ ÿµŸÅÿ≠ÿßÿ™
  const [charSearch, setSearchChar] = useState("");

  //============== üìç ⁄Øÿ±ŸÅÿ™ŸÜ ŸÖÿ≠ÿµŸàŸÑÿßÿ™ ===================
  const handleGetProducts = async (page, count, char) => {
    const res = await getProductsService(page, count, char);

    if (res.status == 200) {
      setData(res.data.data);
      setPageCount(res.data.last_page);
    }
  };
  useEffect(() => {
    handleGetProducts(currentPage, countOfProducts_everyPage, charSearch);
  }, [currentPage, charSearch]);

  //============== üìç ŸáŸÜÿØŸÑ ÿ≥ÿ±⁄Ü ÿ®ÿß⁄©ÿ≥ ===================
  const handleSearch = (char) => {
    setSearchChar(char);
    handleGetProducts(1, countOfProducts_everyPage, char);
  };

  let timeOut;
  const handleSetSearchChar = (char) => {
    clearTimeout(timeOut);
    timeOut = setTimeout(() => {
      handleSearch(char);
    }, 1000);
  };
  //===================================================
  useEffect(() => {
    let pArr = [];
    for (let i = 1; i <= pageCount; i++) pArr.push(i);
    setListOfPages(pArr);
  }, [pageCount]);

  return (
    <div className="p-4 reusable_table">
      <UpperPartPages title="ŸÖÿØ€åÿ±€åÿ™ ŸÖÿ≠ÿµŸàŸÑ" />
      {/* =========== üìç start paginated table =============== */}

      <div className="d-flex justify-content-between align-items-center p-2">
        {/* ================== üîç start searchBoxüëá ================== */}
        <div className="input-group mb-3 searchBox">
          <span className="input-group-text searchButton " id="basic-addon2">
            ÿ¨ÿ≥ÿ™ÿ¨Ÿà
          </span>
          <input
            type="text"
            className="form-control searchInput"
            placeholder="ÿπŸÜŸàÿßŸÜ ÿ±ÿß ÿ¨ÿ≥ÿ™ÿ¨Ÿà ⁄©ŸÜ€åÿØ"
            onChange={(e) => handleSetSearchChar(e.target.value)}
          />
        </div>

        {/* ================== üîç end searchBoxüëÜ ==================== */}

        {/* ================== ‚ûï start show_addButtonüëá ================== */}
        {/* in icon ‚ûï  */}

        <NavLink>
          <AddItem_btn />
        </NavLink>

        {/* ================== ‚ûï end show_addButtonüëÜ ==================== */}
      </div>
      {/* ================= üìçstart tableüëá ================== */}
      <Table data={data} nameOfColumn={nameOfColumn} />

      <Pagination
        pageCount={pageCount}
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
        listOfPages={listOfPages}
      />

    </div>
  );
};
