import swal from "sweetalert";
import { httpService } from "./httpService";

export const getcategoreisService = (id = null) => {
  return httpService(`admin/categories${id ? `?parent=${id}` : ""}`, "get");
};

const handleGetCategoreis = async () => {
  const res = await  httpService(`admin/categories${id ? `?parent=${id}` : ""}`, "get");
  try {
    if (res.status == 200) {
      setData(res.data.data);
    } else {
      swal("خطا!", "مشکلی رخ داده است");
    }
  } catch {
    swal("خطا!", "مشکلی رخ داده است");
    console.log("error catch");
  }
};
