import * as Yup from "yup";
import {
  createNewGuaranteeService,
  updateGuaranteeService,
} from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

//====================== üìçinitialValues =====================
export const initialValues = {
  title: "",
  descriptions: "",
  length: "",
  length_unit: "",
};
//====================== üìçonSubmit ===========================
export const onSubmit = async (
  values,
  actions,
  setData,
  guaranteeToEdit,
  setGuaranteeToEdit
) => {
  console.log(guaranteeToEdit);
  if (guaranteeToEdit.length) {
    const res = await updateGuaranteeService(values, guaranteeToEdit.id);
    if (res.status == 200) {
      setData(res.data.data);
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!", res.data.message, "success");
      // actions.resetForm();
      // console.log(res);
      // console.log(guaranteeToEdit);
      // console.log(values);


      setData((lastData) => {
        let newData = [...lastData];
        let index = newData.findIndex((d) => d.id == brandToEdit.id);
        newData[index] = res.data.data;
        return newData;
      });
      setData(res.data.data)
      actions.resetForm();


    }
  } else {
    const res = await createNewGuaranteeService(values);
    if (res.status == 201) {
      setData((data) => [...data, res.data.data]);
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!", res.data.message, "success");
      actions.resetForm();
    }
  }
};
//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({});
