import React from "react";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import { Form, Formik } from "formik";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_brands";

export const AddNewBrands = () => {
  return (
    // <div className="container ">
    //   {/* ============= */}
    //   <div>
    //     <div className="d-flex justify-content-between pt-3">
    //       <div className="fs-4">افزودن برند جدید</div>
    //       <PrevPageBtn
    //         customStyle="fs-2 text-start text-secondary"
    //         returnTitle={<i className="fa-solid fa-xmark"></i>}
    //       />
    //     </div>
    //     <hr />
    //   </div>

    //   {/* =========== */}
    //   <div>
    //     <Formik
    //       initialValues={initialValues}
    //       onSubmit={onSubmit}
    //       validationSchema={validationSchema}
    //     >
    //       {(formik) => {
    //         console.log(formik);
    //         return (
    //           <Form>
    //             <FormikControl
    //               control="input"
    //               type="text"
    //               name="original_name"
    //               placeholder="عنوان لاتین"
    //             />

    //             <FormikControl
    //               control="input"
    //               type="text"
    //               name="persian_name"
    //               placeholder="عنوان فارسی"
    //             />

    //             <FormikControl
    //               control="textArea"
    //               type="text"
    //               name="descriptions"
    //               placeholder="توضیحات"
    //             />

    //             <FormikControl
    //               formik={formik}
    //               control="addFile"
    //               type="text"
    //               label="تصویر"
    //               name="logo"
    //               placeholder="برای افزودن تصویر اینجا کلیک کنید"
    //             />
    //             <div className="text-center">
    //               <SubmitBtn />
    //             </div>
    //           </Form>
    //         );
    //       }}
    //     </Formik>
    //   </div>
    //   {/* ================ */}
    // </div>
    <>
      <button
        type="button"
        className="btn btn-primary"
        data-bs-toggle="modal"
        data-bs-target="#staticBackdrop"
      >
        Launch static backdrop modal
      </button>

      <div
        className="modal fade modal-dialog modal-dialog-centered"
        id="staticBackdrop"
        data-bs-backdrop="static"
        data-bs-keyboard="false"
        tabindex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header bg-danger">
              <h1 className=" w-100 fs-5" id="staticBackdropLabel">
                افزودن برند جدید
              </h1>
              <button
                type="button"
                className="btn-close "
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div className="modal-body ">
              <Formik
                initialValues={initialValues}
                onSubmit={onSubmit}
                validationSchema={validationSchema}
              >
                {(formik) => {
                  console.log(formik);
                  return (
                    <Form>
                      <FormikControl
                        control="input"
                        type="text"
                        name="original_name"
                        placeholder="عنوان لاتین"
                      />

                      <FormikControl
                        control="input"
                        type="text"
                        name="persian_name"
                        placeholder="عنوان فارسی"
                      />

                      <FormikControl
                        control="textArea"
                        type="text"
                        name="descriptions"
                        placeholder="توضیحات"
                      />

                      <FormikControl
                        formik={formik}
                        control="addFile"
                        type="text"
                        label="تصویر"
                        name="logo"
                        placeholder="برای افزودن تصویر اینجا کلیک کنید"
                      />
                      <div className="text-center">
                        <SubmitBtn />
                      </div>
                    </Form>
                  );
                }}
              </Formik>
            </div>
            {/* <div className="modal-footer bg-info"></div> */}
          </div>
        </div>
      </div>
    </>
  );
};
