import * as Yup from "yup";
import {
  createNewGuaranteeService,
  updateGuaranteeService,
} from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

//====================== üìçinitialValues =====================
export const initialValues = {
  title: "",
  descriptions: "",
  length: "",
  length_unit: "",
};
//====================== üìçonSubmit ===========================
export const onSubmit = async (
  values,
  actions,
  setData,
  guaranteeToEdit,
  setGuaranteeToEdit,
  lengthUnit
) => {
  if (guaranteeToEdit?.length) {
    const newValues = {
      ...values,
      length_unit: lengthUnit,
    };
    const res = await updateGuaranteeService(newValues, guaranteeToEdit.id);
    if (res.status == 200) {
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!", res.data.message, "success");

      setData((oldData) => {
        let newData = [...oldData];
        let index = oldData.findIndex((d) => d.id == guaranteeToEdit.id);
        newData[index] = res.data.data;
        return newData;
      });
      setGuaranteeToEdit(res.data.data);
    }
  } else {
    const newValues = {
      ...values,
      length_unit: lengthUnit,
    };

    const res = await createNewGuaranteeService(newValues);

    if (res.status == 201) {
      setData((data) => [...data, res.data.data]);
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!", res.data.message, "success");
      actions.resetForm();
    }
  }
};
//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({
 title: Yup.string()
  .required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ")
  .matches(/^[a-zA-Z0-9\s@!%$?&]+$/, "ŸÅŸÇÿ∑ ÿßÿ≤ ÿßÿπÿØÿßÿØ Ÿà ÿ≠ÿ±ŸàŸÅ ŸÑÿßÿ™€åŸÜ ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿ¥ŸàÿØ"),
  length :Yup.string().when(' length_unit',{
    is:"",
    then
  })
});
