import * as Yup from "yup";
import {
  createNewBrandService,
  updateBrandService,
} from "../../../services/CRUD_categoryService";
import swal from "sweetalert";
import { useEffect } from "react";

//====================== üìçinitialValues =====================
export const initialValues = {
  original_name: "",
  persian_name: "",
  descriptions: "",
  logo: null,
};

//====================== üìçonSubmit ===========================
export const onSubmit = async (
  data,
  actions,
  tableData,
  setTableData,
  brandToEdit,
  brandId
) => {
  if (brandToEdit?.id) {
    // console.log(brandToEdit);
    const res = await updateBrandService(brandId, brandToEdit);
    // console.log(res);
    if (res.status == 200) {
      swal("ÿßÿπŸÖÿßŸÑ ÿ¥ÿØ", res.data.message, "success");
      setTableData([...tableData, res.data.data]);
      actions.resetForm();
    } else {
      actions.resetForm();
    }
  } else {
    const res = await createNewBrandService(data);
    if (res.status == 201) {
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ", res.data.message, "success");
      setTableData([...tableData, res.data.data]);
      actions.resetForm();
    } else {
      actions.resetForm();
    }
  }
};

//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({
  original_name: Yup.string()
    .required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ")
    .matches(/^[0-9A-Za-z]{1,}$/, "ŸÅŸÇÿ∑ ÿ≠ÿ±ŸàŸÅ ÿßŸÜ⁄ØŸÑ€åÿ≥€å"),
  persian_name: Yup.string().matches(/^[\u0600-\u06FF\s]+$/, "ŸÅŸÇÿ∑ ÿ≠ÿ±ŸàŸÅ ŸÅÿßÿ±ÿ≥€å"),
  descriptions: Yup.string().matches(
    /^[0-9A-Za-z\u0600-\u06FF\s]+$/,
    "ŸÅŸÇÿ∑ ÿ≠ÿ±ŸàŸÅ Ÿà ÿßÿπÿØÿßÿØ ŸÖÿ¨ÿßÿ≤ŸÜÿØ"
  ),
  logo
});
