import * as Yup from "yup";
import {
  createNewDiscountService,
  updateDiscountService,
} from "../../../services/CRUD_categoryService";
import swal from "sweetalert";
import { ConvertPersianDateToMiladi } from "../../general_compo/utils/ConvertPersianDateToMiladi";
import moment from "jalali-moment";
import { Operation_discounts } from "./Operation_discounts";
import jMoment from "jalali-moment";
// ================  üìçheader of table === ===================
export const handle_header_discountsTable = (
  data,
  setData,
  setDiscountToEdit
) => {
  const header_discountsTable = [
    { field: "id", title: "id" },
    { field: "title", title: "ÿπŸÜŸàÿßŸÜ" },
    { field: "code", title: "⁄©ÿØ ÿ™ÿÆŸÅ€åŸÅ" },
    { field: "percent", title: "ÿØÿ±ÿµÿØ ÿ™ÿÆŸÅ€åŸÅ" },
    {
      field: null,
      title: "Ÿàÿ∂ÿπ€åÿ™",
      elements: (rowData) => (rowData.is_active == 1 ? "ŸÅÿπÿßŸÑ" : "ÿ∫€åÿ±ŸÅÿπÿßŸÑ"),
    },
    {
      field: null,
      title: "ÿ™ÿßÿ±€åÿÆ ÿßŸÜŸÇÿ∂ÿß",
      elements: (data) =>
        moment(data.created_at).locale("fa").format("YYYY/MM/DD"),
    },
    {
      field: null,
      title: "ŸÖÿ±ÿ®Ÿàÿ∑ ÿ®Ÿá",
      elements: (rowData) => (rowData.for_all ? "ŸáŸÖŸá" : "ÿ®ÿ±ÿÆ€å ŸÖÿ≠ÿµŸàŸÑÿßÿ™"),
    },
    {
      field: null,
      title: "ÿπŸÖŸÑ€åÿßÿ™",
      elements: (rowData) => (
        <Operation_discounts
          rowData={rowData}
          data={data}
          setData={setData}
          setDiscountToEdit={setDiscountToEdit}
        />
      ),
    },
  ];
  return header_discountsTable;
};

//====================== üìçinitialValues =====================
export const initialValues = {
  id: "",
  title: "",
  code: "22",
  percent: "",
  is_active: "",
  for_all: true,
  expire_at: "",
  product_ids: "",
};
//====================== üìçonSubmit ===========================
export const onSubmit = async (values, action, discountToEdit) => {
  //üìç --------- ÿßŸÅÿ≤ŸàÿØŸÜ €å⁄© ⁄©ÿØ ÿ™ÿÆŸÅ€åŸÅ ÿ¨ÿØ€åÿØ ---------üëá
  if (Object.keys(discountToEdit).length == 0) {
    const convertValues = {
      ...values,
      expire_at: ConvertPersianDateToMiladi(values.expire_at),
    };
    const res = await createNewDiscountService(convertValues);
    if (res.status == 201) {
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!...", res.data.message, "success");
      action.resetForm();
    }

    //üìç ---------Ÿà€åÿ±ÿß€åÿ¥ ⁄©ÿØ ÿ™ÿÆŸÅ€åŸÅ ŸÖŸàÿ¨ŸàÿØ ---------üëá
  } else {
    const convertValues = {
      ...values,
      expire_at: jMoment(values.expire_at, "jYYYY /jMM/jDD").format("YYYY-M-D"),
    };

    const res = await updateDiscountService(discountToEdit.id, convertValues);
    if (res.status == 200) {
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!...", res.data.message, "success");
    }
  }
};

//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object().shape({
  title: Yup.string()
    .required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ")
    .matches(
      /^[\u0600-\u06FF\sa-zA-Z0-9@!%-.$?&]+$/,
      "ŸÅŸÇÿ∑ ÿßÿ≤ ÿ≠ÿ±ŸàŸÅ Ÿà ÿßÿπÿØÿßÿØ ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿ¥ŸàÿØ"
    ),
  expire_at: Yup.string()
    .required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ")
    .matches(/^[0-9/\ \s-]+$/, "ŸÅŸÇÿ∑ ÿßÿ≤ÿßÿπÿØÿßÿØ Ÿà ÿÆÿ∑ ÿ™€åÿ±Ÿá ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿ¥ŸàÿØ"),
  code: Yup.string().required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ"),
  // .matches(/^[a-zA-Z0-9\s@!%-.$?&]+$/, "ŸÅŸÇÿ∑ ÿßÿ≤ ÿ≠ÿ±ŸàŸÅ Ÿà ÿßÿπÿØÿßÿØ ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿ¥ŸàÿØ"),
  percent: Yup.number().required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ"),
  for_all: Yup.boolean(),
  product_ids: Yup.string().when("for_all", {
    is: false,
    then: () =>
      //ŸÖŸáŸÖ ‚≠ê
      //ÿ™Ÿà Ÿàÿ±⁄òŸÜ Ÿáÿß€å ŸÇÿ®ŸÑ€å ŸÑÿßŸÖ ŸÜÿ®ŸàÿØ ŸÅÿßŸÜ⁄©ÿ¥ŸÜ€å ÿ®ŸÜŸà€åÿ≥€åŸÖ
      //ÿ±Ÿà ÿ≠ÿ™ŸÖÿß ÿ®Ÿá ÿµŸàÿ±ÿ™ ŸÅÿßŸÜ⁄©ÿ¥ŸÜ ÿ®ŸÜŸà€åÿ≥€åŸÖ .then ÿßŸÖÿß ÿ™Ÿà Ÿàÿ±⁄òŸÜ ÿ¨ÿØ€åÿØ ÿ®ÿß€åÿØ
      Yup.string()
        .required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ")
        .matches(/^[0-9\s-]+$/, "ŸÅŸÇÿ∑ ÿßÿ≤ÿßÿπÿØÿßÿØ Ÿà ÿÆÿ∑ ÿ™€åÿ±Ÿá ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿ¥ŸàÿØ"),
  }),
});
