import * as Yup from "yup";
import { createNewAttributeService } from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

export const headers_attributesTable = [
    { field: "id", title: "id" },
    { field: "title", title: "ÿπŸÜŸàÿßŸÜ ŸÖÿ≠ÿµŸàŸÑ" },
    { field: "unit", title: "Ÿàÿßÿ≠ÿØ" },
    { field: "in_filter", title: "ŸÜŸÖÿß€åÿ¥ ÿØÿ± ŸÅ€åŸÑÿ™ÿ±" },
  ];
  
// ==============  üìçinitialValues add attributes ===================
export const initialValues = {
  attributeTitle: "",
  attributeUnit: "",
  switchShowFilter: 0,
};

//================  üìçonSubmit add attributes ======================
export const onSubmit = async (values, actions ,location) => {
  const res = await createNewAttributeService(
    location.state?.categoryId,
    values
  );
  try {
    if (res.status == 201) {
      swal("", res.data.message, "success");
      actions.resetForm();
    }
  } catch {}
};

//===============  üìçvalidationSchema add attributes ===============
export const validationSchema = Yup.object({
  attributeTitle: Yup.string().required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ"),
  attributeUnit: Yup.string().required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ"),
});
