import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";

const numOfRows_singlePage = 5; //ÿØÿ± Ÿáÿ± ÿµŸÅÿ≠Ÿá ⁄ÜŸÜÿØ ÿ±ÿØ€åŸÅ ÿßÿ≤ ÿ≠ÿØŸàŸÑ ŸÜŸÖÿß€åÿ¥ ÿØÿßÿØŸá ÿ¥ŸàÿØ

export const ReusableTable = (props) => {
  const {
    nameOfColumn, //headers of columns in table
    dataOfRows, //rows of table = cole data haye ke darim dakhel table
    placeholder_searchBox,
    go_where, //this icon ‚ûï link to where - hamishe ham link nist
    operation,
    show_addButton,
    show_compo,
    having_searchBox,
  } = props;

  const handleShowCompo = () => {
    document
      .getElementById("compo_for_add_items")
      .classList.remove("hidden_screen");
    document.getElementById("compo_for_add_items").classList.add("show_screen");
  };

  const [dataAnyPage, setDataAnyPage] = useState([]); //slice shodeye dataOfRows
  const [currentPage, setCurrentPage] = useState(1);
  const [countPage, setCountPage] = useState(2); //page_count = dataOfRows.length / numOfRows_singlePage (ÿ™ÿπÿØÿßÿØ ⁄©ŸÑ ÿµŸÅÿ≠ÿßÿ™)
  const [pageArr, setPageArr] = useState([]); //[0,1,2,3,4,...,countPage]
  const [searchData, setSearchData] = useState(dataOfRows);
  const [searchChar, setSearchChar] = useState("");

  useEffect(() => {
    const filtered_dataOfRows = dataOfRows.filter((d) =>
      d.title.includes(searchChar)
    );
    console.log(searchData);
    console.log(filtered_dataOfRows);
    setSearchData(filtered_dataOfRows); //€å⁄© ÿ±Ÿà ÿÆŸàÿØŸÖ ÿØÿßÿØŸÖ
    // ÿ®ÿ≥ÿ™⁄Ø€å ÿØÿßÿ±Ÿá ÿ≥ŸÖÿ™ ÿ≥ÿ±Ÿàÿ± ÿßÿ≤ ŸÖÿß ÿ®ÿÆŸàÿßÿØ ⁄©Ÿá ÿ≥ÿ±⁄Ü ÿ®ÿ± ÿ≠ÿ≥ÿ® ⁄Ü€å ÿ®ÿßÿ¥Ÿá
    //  ⁄ÜŸàŸÜ ŸÖŸÜ ÿØ€åÿ™ÿßŸáÿßŸÖ €å⁄© ÿßÿ±ÿß€åŸá ÿßÿ≤ ÿßÿ±ÿß€åŸá Ÿáÿß ÿ®ŸàÿØ ÿß€åŸÜÿ∑Ÿàÿ±€å ÿ¥ÿØ
    //  ÿ®ÿß€åÿØ ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿØÿ± ÿßÿØÿßŸÖŸá ÿßÿµŸÑÿßÿ≠ ÿ®ÿ¥Ÿá
    //ÿß⁄Øÿ± ÿ¥ÿØ €å⁄© ÿßÿ±ÿß€åŸá ÿßÿ≤ ÿßÿ®ÿ¨⁄©ÿ™ Ÿáÿß ÿß€åŸÜÿ∑Ÿàÿ±€å ŸÖ€åÿ¥Ÿá ======> d.folan.includes(searchChar)
    //folan == ⁄Ü€åÿ≤€å ⁄©Ÿá ÿßÿ≤ŸÖŸàŸÜ ÿÆŸàÿßÿ≥ÿ™ŸÜÿØ ÿ®ÿ± ÿ≠ÿ≥ÿ®ÿ¥ ŸÅ€åŸÑÿ™ÿ± ÿ±Ÿà ÿ®ÿ≤ŸÜ€åŸÖ
  }, [searchChar]);

  // useEffect(() => {
  //   //in useEffect , row ha ro be tedad page ha slice mikone
  //   const start = currentPage * numOfRows_singlePage - numOfRows_singlePage;
  //   const end = currentPage * numOfRows_singlePage;
  //   setDataAnyPage(searchData.slice(start, end));
  // }, [currentPage, searchData]);

  // useEffect(() => {
  //   // Math.ceil() ==> round to up number
  //   const page_count = Math.ceil(searchData.length / numOfRows_singlePage);
  //   setCountPage(page_count);
  //   // ========================= //
  //   let arrOfPages = [];
  //   for (let i = 1; i <= page_count; i++) arrOfPages = [...arrOfPages, i]; //[0,1,2,3,4,...,dataOfRows.length]
  //   setPageArr(arrOfPages);
  //   setCurrentPage(1);
  // }, [searchData]);
  return (
    <div className="p-1 reusable_table">
      <div className="d-flex justify-content-between align-items-center p-2">
        {/* ================== start searchBoxüëá ================== */}
        {having_searchBox ? (
          <div className="input-group mb-3 searchBox">
            <span className="input-group-text searchButton " id="basic-addon2">
              ÿ¨ÿ≥ÿ™ÿ¨Ÿà
            </span>
            <input
              type="text"
              className="form-control searchInput"
              placeholder={placeholder_searchBox}
              onChange={(e) => setSearchChar(e.target.value)}
            />
          </div>
        ) : null}

        {/* ================== end searchBoxüëÜ ==================== */}
        {/* in icon ‚ûï  */}
        {show_addButton ? ( //ÿßÿ∂ÿßŸÅŸá ⁄©ÿ±ÿØŸÜ ÿ¢€åÿ™ŸÖ
          <NavLink to={go_where}>
            <div className="d-flex justify-content-end">
              <button
                onClick={() => (show_compo ? handleShowCompo() : "")}
                className="btn btn-success d-flex justify-content-center align-items-center"
              >
                <i className="fas fa-plus text-light"></i>
              </button>
            </div>
          </NavLink>
        ) : (
          <div></div>
        )}
      </div>
      {/* ================== start tableüëá ====================== */}
      <table className=" table_of_reusable_table table table-responsive text-center table-hover table-bordered">
        <thead className="table-secondary"></thead>
        <tbody>
          {dataAnyPage.map((i, index) => (
            // <>
            <tr key={Math.random()}>
              {dataAnyPage[index].map((i) => (
                <td key={Math.random()}>{i}</td>
              ))}
              {operation ? (
                <td>
                  <i
                    className="fas fa-project-diagram text-info mx-1 hoverable_text pointer has_tooltip"
                    title="ÿ≤€åÿ±ŸÖÿ¨ŸÖŸàÿπŸá"
                    data-bs-toggle="tooltip"
                    data-bs-placement="top"
                  ></i>
                  <i
                    className="icon_product_table fas fa-edit text-warning mx-1 pointer has_tooltip"
                    title="Ÿà€åÿ±ÿß€åÿ¥ ŸÖÿ≠ÿµŸàŸÑ"
                    data-bs-toggle="modal"
                    data-bs-placement="top"
                    data-bs-target="#add_product_modal"
                  ></i>
                  <i
                    className="icon_product_table fas fa-receipt text-success mx-1 pointer has_tooltip"
                    title="ÿßŸÅÿ≤ŸàÿØŸÜ Ÿà€å⁄ò⁄Ø€å"
                    data-bs-toggle="modal"
                    data-bs-target="#add_product_attr_modal"
                  ></i>
                  <i
                    className="icon_product_table fas fa-times text-danger mx-1 pointer has_tooltip"
                    title="ÿ≠ÿ∞ŸÅ ŸÖÿ≠ÿµŸàŸÑ"
                    data-bs-toggle="tooltip"
                    data-bs-placement="top"
                  ></i>
                </td>
              ) : (
                ""
              )}
            </tr>
            // </>
          ))}
        </tbody>
      </table>
      {/* ================== end tableüëÜ ======================== */}

      {/* ================== start paginationüëá ================= */}

      {/*üèÆüèÆüèÆ agar tedad safahat 1 bood 
      Pagination namayesh dade nashe üëá*/}

      {countPage > 1 ? (
        <nav
          aria-label="Page navigation example"
          className="d-flex justify-content-center"
        >
          <ul className="pagination dir_ltr">
            <li className="page-item">
              <span
                className={`page-link pointer ${
                  currentPage == 1 ? "disabled_pagination" : ""
                }`}
                aria-label="Previous"
                onClick={() => setCurrentPage(currentPage - 1)}
              >
                <span aria-hidden="true">&raquo;</span>
              </span>
            </li>
            {pageArr.map((i) => (
              <li className="page-item" key={Math.random()}>
                <span
                  className="page-link pointer
              "
                  onClick={() => setCurrentPage(i)}
                >
                  {i}
                </span>
              </li>
            ))}

            <li className="page-item">
              <span
                className={`page-link pointer ${
                  currentPage == countPage ? "disabled_pagination" : ""
                }`}
                aria-label="Next"
                onClick={() => setCurrentPage(currentPage + 1)}
              >
                <span aria-hidden="true">&laquo;</span>
              </span>
            </li>
          </ul>
        </nav>
      ) : (
        ""
      )}
      {/* ================== end paginationüëÜ =================== */}
    </div>
  );
};
