import React from "react";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import { Form, Formik } from "formik";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_products";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import { useState } from "react";
import { useEffect } from "react";
import {
  getAllBrandsService,
  getAllColorService,
  getCategoriesService,
} from "../../../../services/CRUD_categoryService";
import { useLocation } from "react-router-dom";

export const AddProduct = () => {
  const [mainCategories, setMainCategories] = useState([]); // ŸÜ⁄ØŸáÿØÿßÿ±€å ŸáŸÖŸá ŸÖÿ≠ÿµŸàŸÑÿßÿ™ ŸàÿßŸÑÿØ
  const [colors, setColors] = useState([]);
  const [brands, setBrands] = useState([]);
  const [reInitialize, setReInitialize] = useState([]);
  const location = useLocation();

  useEffect(() => {
    setReInitialize({
      category_ids: "location.state.rowData",
      title: location.state.rowData?.title,
      price: location.state.rowData?.price,
      weight: location.state.rowData?.weight,
      brand_id: location.state.rowData?.brand_id,
      color_ids: "location.state.rowData",
      guarantee_ids: location.state.rowData?.guarantee_ids,
      descriptions: location.state.rowData?.descriptions,
      short_descriptions: location.state?.rowData.short_descriptions,
      cart_descriptions: location.state?.rowData.cart_descriptions,
      image: "image",
      alt_image: location.state.rowData.alt_image,
      discount: location.state.rowData.discount,
      keywords: location.state.rowData.keywords,
      stock: location.state.rowData.stock,
    });
    console.log(location.state.rowData);
  }, [location]);
  //=========== üëá ⁄Øÿ±ŸÅÿ™ŸÜ ŸáŸÖŸá ŸÖÿ≠ÿµŸàŸÑÿßÿ™ ŸàÿßŸÑÿØ =============
  const handleGetCategories = async () => {
    const res = await getCategoriesService();
    if (res.status == 200) {
      if (typeof res.data.data == "object") {
        for (const key in res.data.data) {
          setMainCategories((oldData) => [
            ...oldData,
            { id: res.data.data[key].id, value: res.data.data[key].title },
          ]);
        }
      } else {
        console.log("its not object");
      }
    }
  };

  useEffect(() => {
    handleGetCategories();
  }, []);
  //============ üëÜ ⁄Øÿ±ŸÅÿ™ŸÜ ŸáŸÖŸá ŸÖÿ≠ÿµŸàŸÑÿßÿ™ ŸàÿßŸÑÿØ =============
  //=============== üëá ⁄Øÿ±ŸÅÿ™ŸÜ ÿ±ŸÜ⁄Ø Ÿáÿß ===================
  const handleGetColors = async () => {
    const res = await getAllColorService();
    if (res.status == 200) {
      if (typeof res.data.data == "object") {
        for (const key in res.data.data) {
          setColors((oldData) => [
            ...oldData,
            { id: res.data.data[key].id, value: res.data.data[key].title },
          ]);
        }
      } else {
        console.log("its not object");
      }
    }
  };
  useEffect(() => {
    handleGetColors();
  }, []);
  //=============== üëÜ ⁄Øÿ±ŸÅÿ™ŸÜ ÿ±ŸÜ⁄Ø Ÿáÿß ===================
  // ============== üëá ⁄Øÿ±ŸÅÿ™ŸÜ ÿ®ÿ±ŸÜÿØ Ÿáÿß ===================
  const handleGetBrands = async () => {
    const res = await getAllBrandsService();
    if (res.status == 200) {
      if (typeof res.data.data == "object") {
        for (const key in res.data.data) {
          setBrands((oldData) => [
            ...oldData,
            {
              id: res.data.data[key].id,
              value: res.data.data[key].persian_name,
            },
          ]);
        }
      } else {
        console.log("its not object");
      }
    }
  };
  useEffect(() => {
    handleGetBrands();
  }, []);
  // ============== üëÜ ⁄Øÿ±ŸÅÿ™ŸÜ ÿ®ÿ±ŸÜÿØ Ÿáÿß ===================
  return (
    <div>
      {/* ================= üìç header of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container">
        <h5>
          {location.state.title_for_adding
            ? location.state.title_for_adding
            : location.state.title_for_editing}
        </h5>
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>

      {/* =================== üìç start form  ======================= */}
      <div className="container col-lg-5 ">
        <Formik
          initialValues={reInitialize || initialValues}
          onSubmit={(rowData) => onSubmit(rowData)}
          validationSchema={validationSchema}
          enableReinitialize
        >
          {(form) => {
            console.log(form.values);
            return (
              <Form>
                {mainCategories.length ? (
                  <FormikControl
                    control="select"
                    label="ÿØÿ≥ÿ™Ÿá"
                    option={mainCategories}
                    name="category_ids"
                  />
                ) : (
                  "ÿØÿ≥ÿ™Ÿá ŸàÿßŸÑÿØ"
                )}
                <FormikControl
                  control="input"
                  type="text"
                  name="title"
                  placeholder="ÿπŸÜŸàÿßŸÜ"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="price"
                  title="ŸÇ€åŸÖÿ™"
                  placeholder="ŸÇ€åŸÖÿ™ (ÿ™ŸàŸÖÿßŸÜ)"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="weight"
                  title="Ÿàÿ≤ŸÜ"
                  placeholder="Ÿàÿ≤ŸÜ (⁄©€åŸÑŸà⁄Øÿ±ŸÖ)"
                />
                {brands.length ? (
                  <FormikControl
                    control="select"
                    label="ÿ®ÿ±ŸÜÿØ"
                    option={brands}
                    name="brand_id"
                  />
                ) : null}
                {colors.length ? (
                  <FormikControl
                    control="select"
                    label="ÿ±ŸÜ⁄Ø"
                    option={colors}
                    name="color_ids"
                  />
                ) : (
                  "colors"
                )}
                {/* <FormikControl
                  control="select"
                  label="⁄Øÿßÿ±ÿßŸÜÿ™€å"
                  option={}
                  name="guarantee_ids"
                />  */}
                <FormikControl
                  control="textArea"
                  type="type"
                  name="descriptions"
                  placeholder="ÿ™Ÿàÿ∂€åÿ≠ÿßÿ™"
                />
                <FormikControl
                  control="addFile"
                  formik={form}
                  label="ÿ™ÿµŸà€åÿ±"
                  name="image"
                  placeholder="üìÇ ÿ®ÿ±ÿß€å ÿßŸÜÿ™ÿÆÿßÿ® ÿ™ÿµŸà€åÿ± ÿß€åŸÜÿ¨ÿß ⁄©ŸÑ€å⁄© ⁄©ŸÜ€åÿØ"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="alt_image"
                  placeholder="ÿ™Ÿàÿ∂€åÿ≠ ÿ™ÿµŸà€åÿ±"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="keywords"
                  placeholder=" ÿ™⁄Ø Ÿáÿß"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="stock"
                  title="ŸÖŸàÿ¨ŸàÿØ"
                  placeholder="ŸÖŸàÿ¨ŸàÿØ€å (ÿ™ÿπÿØÿßÿØ)"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="discount"
                  title="ÿ™ÿÆŸÅ€åŸÅ"
                  placeholder="ÿØÿ±ÿµÿØ ÿ™ÿÆŸÅ€åŸÅ"
                />
                <div className=" text-center pt-4">
                  <SubmitBtn />
                </div>
              </Form>
            );
          }}
        </Formik>
      </div>
    </div>
  );
};
