import React from "react";
import { ItemsOfList } from "./ItemsOfList";

export const Sidebar = () => {
  return (
    <section className="sidebar_section">
      <div className="mini_sidebar bg-dark vh-100">
        <ul className="p-0">
          <ItemsOfList item="داشبورد" icon="fa-tachometer-alt" pointer='pointer hover_items_sidebar'/>
          {/* ======================= فروشگاه ======================== */}
          <ItemsOfList item="فروشگاه" titleStyle="titleStyle w-100 fs-5" pointer='pointer hover_items_sidebar'/>
          <ItemsOfList item="مدیریت گروه محصولات" icon="fa-stream" pointer='pointer hover_items_sidebar'/>
          <ItemsOfList item="مدیریت محصول" icon="fa-cube" pointer='pointer hover_items_sidebar'/>
          <ItemsOfList item="مدیریت برندها" icon="fa-copyright" pointer='pointer hover_items_sidebar'/>
          <ItemsOfList item="مدیریت گارانتی ها" icon="fa-pagelines" pointer='pointer hover_items_sidebar'/>
          <ItemsOfList item="مدیریت رنگ ها" icon="fa-palette" pointer='pointer hover_items_sidebar'/>
          <ItemsOfList item="مدیریت تخفیف ها" icon="fa-percentage" pointer='pointer hover_items_sidebar' />
          {/* ======================= سفارشات و سبد ======================== */}
          <ItemsOfList
            item="سفارشات و سبد "
            titleStyle="titleStyle w-100 fs-5"
          />
          <ItemsOfList item="مدیریت سبدها" icon="fa-shopping-basket" pointer='pointer hover_items_sidebar'/>
          <ItemsOfList item="مدیریرت سفارشات" icon="fa-luggage-cart" pointer='pointer hover_items_sidebar'/>
          <ItemsOfList item="مدیریت نحوه ارسال" icon="fa-truck-loading" pointer='pointer hover_items_sidebar'/>
          {/* ======================= کاربران و همکاران ======================== */}
          <ItemsOfList
            item="کاربران و همکاران "
            titleStyle="titleStyle w-100 fs-5"
          />
          <ItemsOfList item="مشاهده کاربران" icon="fa-users" pointer='pointer hover_items_sidebar'/>
          <ItemsOfList item="نفش ها" icon="fa-user-tag" pointer='pointer hover_items_sidebar'/>
          <ItemsOfList item="مجوزها" icon="fa-shield-alt" pointer='pointer hover_items_sidebar'/>
          {/* ======================= ارتباطات ======================== */}
          <ItemsOfList item="ارتباطات" titleStyle="titleStyle w-100 fs-5" />
          <ItemsOfList item="سوالات" icon="fa-question-circle" pointer='pointer hover_items_sidebar'/>
        </ul>
      </div>
    </section>
  );
};
