import { FastField } from "formik";
import React from "react";
import { DatePicker } from "react-advance-jalaali-datepicker";

export const PersianDate = ({ label, type, name }) => {
  return (
    <div>
      <div className="input-group">
        <span className="input-group-text titleSpan customStyle">{label}</span>
        <FastField type={type} name={name} className="form-control">
          {() => {
            return (
              <div className="form-control testStyle ms-2">
                <DatePicker
                //   className="testStyle"
                  // inputComponent={this.DatePickerInput}
                  placeholder="1340/05/15 به طور مثال"
                  format="jYYYY/jMM/jDD"
                  // onChange={this.change}
                  // id="datePicker"
                  // preSelected="1396/05/15 به طور مثال"
                />
              </div>
            );
          }}
        </FastField>
      </div>
    </div>
  );
};
