import { Formik, Form } from "formik";
import React from "react";
import * as Yup from "yup";
import { FormikControl } from "./FormikControl_AddItems";
import {
  createNewCategoryService,
  getCategoriesService,
} from "../../services/category";
import { useEffect } from "react";
import { useState } from "react";
import swal from "sweetalert";

export const FormikAddItems = () => {

  //=================================================
  const initialValues = {
    title: "",
    description: "",
    parent_id: "",
    is_active: true,
    show_in_menu: true,
    image: null,
  };

  //=================================================

  const onSubmit = async (values, actions) => {
   
  };

  const validationSchema = Yup.object({
    parent_id: Yup.number(),
    title: Yup.string()
      .required("لطفا این قسمت را پر کنید")
      .matches(
        /^[\u0600-\u06FF\sa-zA-Z0-9@!%$?&]+$/,
        "فقط از حروف و اعداد استفاده شود"
      ),
    description: Yup.string().matches(
      /^[\u0600-\u06FF\sa-zA-Z0-9@!%$?&]+$/,
      "فقط از حروف و اعداد استفاده شود"
    ),
    image: Yup.mixed()
      .test("filesize", "حجم فایل نمیتواند بیشتر 500 کیلوبایت باشد", (value) =>
        !value ? true : value.size <= 500 * 1024
      )
      .test("format", "فرمت فایل باید jpg باشد", (value) =>
        !value ? true : value.type === "image/jpeg"
      ),
    is_active: Yup.boolean(),
    show_in_menu: Yup.boolean(),
  });

  const [parents, setParents] = useState([]);
  const handleGetParentsCategories = async () => {
    try {
      const res = await getCategoriesService();
      if (res.status == 200) {
        const allParents = res.data.data;
        setParents(
          allParents.map((p) => {
            return { id: p.id, value: p.title };
          })
        );
      }
    } catch (error) {
      swal("مشکل...!", "متاسفانه دسته بندی های والد دریافت نشد", "warning");
    }
  };
  useEffect(() => {
    handleGetParentsCategories();
  }, []);

  return (
    <Formik
      initialValues={initialValues}
      onSubmit={(values, actions) => onSubmit(values, actions)}
      validationSchema={validationSchema}
    >
      {(formik) => {
        //console.log(formik);
        return (
          <Form className="">
            {parents.length > 0 ? (
              <FormikControl
                control="select"
                type="select"
                name="parent_id"
                label="دسته والد"
                options={parents}
              />
            ) : null}
            <FormikControl
              formik={formik}
              control="input"
              type="text"
              name="title"
              placeholder="عنوان"
              inputStyle="registerInputStyle"
            />
            <FormikControl
              control="textArea"
              type="textarea"
              name="description"
              placeholder="توضیحات"
            />
            <FormikControl
              control="addFile"
              type="text"
              name="addFile"
              placeholder="برای انتخاب تصویر خود کلیک کنید."
            />
            <FormikControl
              control="SwitchCheckBox"
              name="is_active"
              label="وضعیت فعال"
            />

            <FormikControl
              control="SwitchCheckBox"
              name="show_in_menu"
              label="نمایش در منو"
            />
            <button type="submit" className="btn btn-success">
              ذخیره
            </button>
          </Form>
        );
      }}
    </Formik>
  );
};
