import React from "react";
import { UpperPartPages } from "../../general_compo/reusable_operations/UpperPartPages";
import { Reusable_table2 } from "../../general_compo/reusable_table/reusable_table2/Reusable_table2";
import { useEffect } from "react";
import { getProductsService } from "../../../services/CRUD_categoryService";
import { useState } from "react";
import { Operation_products } from "./table_addition/Operation_products";

export const ProductManagement = () => {
  //========= هدرهای جدول📍==============
  const headers_productTable = [
    { field: "id", title: "#" },
    {
      field: null,
      title: "گروه محصول",
      elements: (rowData) => rowData.categories[0].title,
    },
    { field: "title", title: "عنوان" },
    { field: "price", title: "قیمت" },
    { field: "stock", title: "موجودی" },
    {
      field: null,
      title: "عملیات",
      elements: (rowData) => <Operation_products rowData={rowData} />,
    },
  ];
  const [data, setData] = useState([]);

  const handleGetProducts = async () => {
    const res = await getProductsService(null, null,null);
    console.log(res);
    if (res.status == 200) {
      setData(res.data.data);
    }
  };

  useEffect(() => {
    handleGetProducts();
  }, []);

  return (
    <div>
      <UpperPartPages title="مدیریت محصول" />
      <Reusable_table2 data={data} nameOfColumn={headers_productTable} />
    </div>
  );
};
