import jMoment from "jalali-moment";

export const ConvertPersianDateToMiladi = (date) => {
  const persianDateObject = jMoment(date, "jYYYY/jM/jD");
  const miladiDate = persianDateObject.toDate(); // تبدیل به تاریخ میلادی
  const year = miladiDate.getFullYear();
  const month = (miladiDate.getMonth() + 1).toString().padStart(2, "0");
  const day = miladiDate.getDate().toString().padStart(2, "0");
  const miladiDateString = `${year}-${month}-${day}`;
  return miladiDateString;
};
