import { Formik, Form } from "formik";
import React from "react";
import * as Yup from "yup";
import { FormikControl } from "./FormikControl_AddItems";

export const FormikAddItems = () => {
  const initialValues = {
    title: "title",
  };
  return (
    <Formik initialValues={initialValues} onSubmit={() => console.log("test")}>
      {(formik) => {
        console.log(formik);
        return (
          <div className="d-flex ">
            <Form className="bg-danger">
              <FormikControl
                formik={formik}
                control="input"
                type="text"
                name="عنوان"
                placeholder="عنوان"
                inputStyle="registerInputStyle"
              />
              <FormikControl control="textArea" />
              <FormikControl control="addFile" />
              <FormikControl control="checkbox" />
            </Form>
          </div>
        );
      }}
    </Formik>
  );
};
