import React from "react";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import { Form, Formik } from "formik";
import {
  initialValues,
  validationSchema,
} from "../../5-colour/FormikHelper_colour";
import { onSubmit } from "../FormikHelper_products";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import { useState } from "react";
import { useEffect } from "react";
import { getCategoriesService } from "../../../../services/CRUD_categoryService";
import { useLocation } from "react-router-dom";

export const AddProduct = () => {
  const [mainCategories, setMainCategories] = useState([]);
  const [mainCategoryID, setMainCategoryID] = useState([]);
  const [newProduct, setNewProduct] = useState([]);
  const location = useLocation();

  const handleGetCategories = async () => {
    const res = await getCategoriesService();
    if (res.status == 200) {
      //console.log(res.data.data);
      if (typeof res.data.data == "object") {
        setMainCategories([]);
        for (const key in res.data.data) {
          // console.log(res.data.data[key].title);
          // console.log(res.data.data[key].id);
          setMainCategoryID((prevID) => [...prevID, res.data.data[key].id]);
          setMainCategories((oldData) => [
            ...oldData,
            { id: key, value: res.data.data[key].title },
          ]);
        }
      }
      // setMainCategories(res.data.data);
    }
  };

  useEffect(() => {
    handleGetCategories();
  }, []);

  return (
    <div className="">
      {/* ================= 📍 header of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container">
        <h5>{location.state.title_for_adding}</h5>
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>

      {/* =================== 📍 start form  ======================= */}
      <div className="container col-lg-5 ">
        <Formik
          initialValues={initialValues}
          onSubmit={() => onSubmit(newProduct)}
          validationSchema={validationSchema}
        >
          {(form) => {
            // console.log(form.values);
            setNewProduct(form.values);
            return (
              <Form>
                {mainCategories.length ? (
                  <FormikControl
                    control="select"
                    label="دسته"
                    option={mainCategories}
                    name="category_ids"
                    parent_title=""
                  />
                ) : (
                  "دسته والد"
                )}
                <FormikControl
                  control="input"
                  type="text"
                  name="title"
                  placeholder="عنوان"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="price"
                  placeholder="قیمت"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="weight"
                  placeholder="وزن"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="color_ids"
                  placeholder="رنگ"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="guarantee_ids"
                  placeholder="گارانتی"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="keywords"
                  placeholder=" تگ ها"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="stock"
                  placeholder="موجودی"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="discount"
                  placeholder="تخفیف"
                />
                <div className=" text-center pt-4">
                  <SubmitBtn />
                </div>
              </Form>
            );
          }}
        </Formik>
      </div>
    </div>
  );
};
