import { FastField, Form, Formik } from "formik";
import React from "react";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_colour";

export const AddNewColor = ({ colorToEdit }) => {
  return (
    <div className="modal-content">
      <Formik
        initialValues={initialValues}
        onSubmit={onSubmit}
        validationSchema={validationSchema}
      >
        {(formik) => {
          //console.log(formik);
          return (
            <Form>
              {/* ==============  modal-header ============= */}
              <div className="modal-header">
                <h1 className=" w-100 fs-5" id="staticBackdropLabel">
                  اضافه کردن گارانتی جدید
                </h1>
              </div>
              {/* ==============  modal-body ============== */}
              <FormikControl
                control="input"
                type="text"
                name="title"
                placeholder="رنگ"
              />
              <FastField>
                {() => {
                  return (
                    <div >
                      <label htmlFor="code_color">کد رنگ</label>
                      <input name="code" type="color" id="code_color" />
                    </div>
                  );
                }}
              </FastField>
              <div className="text-center">
                <SubmitBtn />
              </div>
            </Form>
          );
        }}
      </Formik>
    </div>
  );
};
