import * as Yup from "yup";
import { createNewDiscountService } from "../../../services/CRUD_categoryService";
import swal from "sweetalert";
import { ConvertPersianDateToMiladi } from "../../general_compo/utils/ConvertPersianDateToMiladi";

// ================  üìçheader of table === ===================
export const header_discountsTable = [
  { field: "id", title: "id" },
  { field: "title", title: "ÿπŸÜŸàÿßŸÜ" },
  { field: "code", title: "⁄©ÿØ ÿ™ÿÆŸÅ€åŸÅ" },
  { field: "percent", title: "ÿØÿ±ÿµÿØ ÿ™ÿÆŸÅ€åŸÅ" },
  {
    field: null,
    title: "Ÿàÿ∂ÿπ€åÿ™",
    elements: (rowData) => (rowData.is_active == 1 ? "ŸÅÿπÿßŸÑ" : "ÿ∫€åÿ±ŸÅÿπÿßŸÑ"),
  },
  {
    title: "ÿ™ÿßÿ±€åÿÆ ÿßŸÜŸÇÿ∂ÿß",
    elements: (data) =>
      moment(data.created_at).locale("fa").format("YYYY/M/D"),
  },
  {
    title: "ÿπŸÖŸÑ€åÿßÿ™",
    elements: (rowData) => (
      <Operation_discounts rowData={rowData} data={data} setData={setData} />
    ),
  },
];

//====================== üìçinitialValues =====================
export const initialValues = {
  id: "",
  title: "",
  code: "",
  percent: "",
  is_active: "",
  for_all: true,
  expire_at: "",
};
//====================== üìçonSubmit ===========================
export const onSubmit = async (values, action) => {
  console.log(values);
  console.log(action);
  const convertValues = {
    ...values,
    expire_at: ConvertPersianDateToMiladi(values.expire_at),
  };
  const res = await createNewDiscountService(convertValues);
  console.log(res);
  if (res.status == 201) {
    swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ!...", res.data.message, "success");
    action.resetForm();
  }
};

//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({
  title: Yup.string()
    .required("ŸÑÿ∑ŸÅÿß ÿß€åŸÜ ŸÇÿ≥ŸÖÿ™ ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ")
    .matches(/^[a-zA-Z0-9\s@!%$?&]+$/, "ŸÅŸÇÿ∑ ÿßÿ≤ ÿßÿπÿØÿßÿØ Ÿà ÿ≠ÿ±ŸàŸÅ ŸÑÿßÿ™€åŸÜ ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿ¥ŸàÿØ"),
});
