import React, { useEffect, useState } from "react";
import { Form, Formik } from "formik";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_brands";

export const AddNewBrands = ({ data, setData }) => {
  const [valueOnModal, setValueOnModal] = useState([]);

  useEffect(() => {
    console.log(valueOnModal);
  }, [valueOnModal]);

  return (
    <Formik
      initialValues={initialValues}
      onSubmit={(values, actions) => {
        onSubmit(values, actions, data, setData);
      }}
      validationSchema={validationSchema}
    >
      {(formik) => {
        console.log(formik);
        <div>
          <button
            type="button"
            className="btn btn-success"
            data-bs-toggle="modal"
            data-bs-target="#staticBackdrop"
          >
            افزودن برند جدید
          </button>
          ;{/* ================= 📍start modal ================= */}
          <div
            className="modal fade modal-dialog modal-dialog-centered"
            id="staticBackdrop"
            data-bs-backdrop="static"
            data-bs-keyboard="false"
            tabIndex="-1"
            aria-labelledby="staticBackdropLabel"
            aria-hidden="true"
          >
            <div className="modal-dialog">
              <div className="modal-content">
                {/* ==============  modal-header ============= */}
                <div className="modal-header">
                  <h1 className=" w-100 fs-5" id="staticBackdropLabel">
                    افزودن برند جدید
                  </h1>
                  {/* =========== ❌ (exit the modal) ========== */}
                  <button
                    type="button"
                    className="btn-close "
                    data-bs-dismiss="modal"
                    aria-label="Close"
                  ></button>
                </div>
                {/* ==============  modal-body ============== */}
                <div className="modal-body ">
                  {/* <Formik
                initialValues={initialValues}
                onSubmit={(values, actions) => {
                  onSubmit(values, actions, data, setData);
                }}
                validationSchema={validationSchema}
              >
                {(formik) => {
                  console.log(formik); */}
                  return (
                  <Form>
                    <FormikControl
                      control="input"
                      type="text"
                      name="original_name"
                      placeholder="عنوان لاتین"
                    />

                    <FormikControl
                      control="input"
                      type="text"
                      name="persian_name"
                      placeholder="عنوان فارسی"
                    />

                    <FormikControl
                      control="textArea"
                      type="text"
                      name="descriptions"
                      placeholder="توضیحات"
                    />

                    <FormikControl
                      formik={formik}
                      control="addFile"
                      type="text"
                      label="تصویر"
                      name="logo"
                      placeholder="برای افزودن تصویر اینجا کلیک کنید"
                    />
                    <div className="text-center">
                      <SubmitBtn />
                    </div>
                  </Form>
                  );
                  {/* }}
              </Formik> */}
                </div>
              </div>
            </div>
          </div>
          ;{/* ================= 📍end modal ================= */}
        </div>;
      }}
    </Formik>
  );
};
