import { ErrorMessage, Field } from "formik";
import React from "react";
import { NavLink } from "react-router-dom";

export const Select_Input = ({
  label,
  option,
  firstItem,
  name,
  parent_title,
  showSubInput,
  stateOfData,
  addBtnPath,
}) => {
  return (
    <div className="p-2 ">
      <div className="input-group mb-1 ">
        <span className="input-group-text w_6rem customStyle">{label}</span>
<div className='bg-danger'>

        <Field>
          {() => {
            return (
              <>
                <Field
                  as="select"
                  className="form-control form-select"
                  id={name}
                  name={name}
                  onClick={(e) => {
                    showSubInput && stateOfData(e.target.value);
                    console.log(e.target.value);
                  }}
                >
                  {parent_title ? (
                    <option>{parent_title}</option>
                  ) : (
                    <>
                      <option hidden value>
                        {firstItem ? firstItem : "--"}
                      </option>
                      {option.map((d) => (
                        <option
                          className="text-dark"
                          key={Math.random()}
                          value={d.id}
                        >
                          {d.value}
                        </option>
                      ))}
                    </>
                  )}
                </Field>
                <div className="d-flex justify-content-center align-items-center">
                  <NavLink className="text-decoration-none" to={"/"}>
                    <button className="btn btn-sm btn-light d-flex">
                      <i className="fas fa-plus text-success"></i>
                    </button>
                  </NavLink>
                </div>
              </>
            );
          }}
        </Field>
</div>
      </div>

      <ErrorMessage
        name={name}
        render={(msg) => <small className="text-danger">{msg}</small>}
      />
    </div>
  );
};

