import * as Yup from "yup";
import {
  createNewRoleService,
  updateRoleService,
} from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

//====================== üìçinitialValues =====================
export const initialValues = {
  id: "",
  title: "",
  description: "",
  permissions: "",
};

//====================== üìçonSubmit ===========================
export const onSubmit = async (values, actions, rowDataToEdit) => {
  if (rowDataToEdit) {
    console.log(rowDataToEdit.id);
    //------- ‚≠ê Ÿà€åÿ±ÿß€åÿ¥ €å⁄©€å ÿßÿ≤ ÿ±ŸÜ⁄Ø Ÿáÿß ---------
    const res = await updateRoleService(rowDataToEdit.id, values);
  } else {
    // -------- ‚≠ê ÿßŸÅÿ≤ŸàÿØŸÜ €å⁄© ÿ±ŸÜ⁄Ø ÿ¨ÿØ€åÿØ ----------
    const res = await createNewRoleService(values);
    if (res.status == 201) {
      swal("ÿ´ÿ®ÿ™ ÿ¥ÿØ", res.data.message, "success");
      console.log(actions);
      actions.resetForm();
    }
  }
};

//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({});
