export const ConvertPersianDateToMiladi = (date) => {
  const persianDateObject = jMoment(date, "jYYYY/jM/jD");
  const miladiDate = persianDateObject.toDate(); // تبدیل به تاریخ میلادی
  const trueMiladiDate = miladiDate.toISOString().split("T")[0]; // بازگرداندن به شکل رشته‌ای "YYYY-MM-DD"

