import React, { useEffect, useState } from "react";
import { Admin } from "../Admin";
import { Login } from "../authorization/login/Login";
import axios from "axios";
import { Navigate, Route, useNavigate } from "react-router-dom";
import { ControlPages } from "../../general_compo/ControlPages";
import { MainPage } from "./MainPage";

export const Dashboard = () => {
  const [isLogin, setIsLogin] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const loginToken = JSON.parse(localStorage.getItem("loginToken"));
    if (loginToken) {
      axios
        .get("https://ecomadminapi.azhadev.ir/api/auth/user", {
          headers: {
            Authorization: `Bearer ${loginToken.token}`,
          },
        })
        .then((res) => {
          console.log("وارد then شد");
          if (res.status == 200) {
            console.log("وارد status=200 شد");
            setIsLogin(true);
          }
          setLoading(false);
        })
        .catch(() => {
          console.log("وارد catch شد");
          localStorage.removeItem("loginToken");
          setIsLogin(false);
          setLoading(false);
        });
    } else {
      console.log("وارد else شد");
      setIsLogin(false);
      setLoading(false);
    }
  }, [loading, isLogin]);
