import * as Yup from "yup";
import { createNewProductService } from "../../../services/CRUD_categoryService";
import swal from "sweetalert";

//====================== üìçinitialValues =====================
export const initialValues = {
  category_ids: "",
  title: "",
  price: "",
  weight: "",
  brand_id: "",
  color_ids: "",
  guarantee_ids: "",
  descriptions: "",
  short_descriptions: "",
  cart_descriptions: "",
  image: "",
  alt_image: "",
  discount: "",
  guarantee_ids: "",
  keywords: "",
  stock: "",
};

//====================== üìçonSubmit ===========================
export const onSubmit = async (rowData) => {
  console.log(rowData);
  const res = await createNewProductService(rowData);
  console.log(res);
  if (res.status == 201) {
    swal('a','s','success')
  }
};

//====================== üìçvalidationSchema ===================
export const validationSchema = Yup.object({});
