import React from "react";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
import { getAttributesService } from "../../../../services/CRUD_categoryService";
import { useState } from "react";
import { Form, Formik } from "formik";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import { onSubmit } from "./FormikHelper_AddAtt_products";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import { GreenSpinner } from "../../../general_compo/spinners&chips/GreenSpinner";
import * as Yup from "yup";
import { invalid } from "jalali-moment";

export const AddAttributes = () => {
  const location = useLocation();
  const [typeAtt, setTypeAtt] = useState();
  const [Id_product, setId_product] = useState([]);
  const [isAnyAttr, setIsAnyAttr] = useState();
  const [isSpinnerOn, setIsSpinnerOn] = useState(true);
  const [initialValues, setInitialValues] = useState(null);
  const [validationSchema, setValidationSchema] = useState({});

  useEffect(() => {
    setId_product(location.state.rowData.id);
  }, [location]);

  setTimeout(() => {
    setIsSpinnerOn(false);
  }, 700);

  //============= ⁄Øÿ±ŸÅÿ™ŸÜ ÿ™ÿß€åŸæ Ÿà€å⁄ò⁄Ø€å Ÿáÿß ÿßÿ≤ ŸÖÿ≠ÿµŸàŸÑ ŸàÿßŸÑÿØ =============

  const handleGetTypeOfAtt = async () => {
    let attrVar = [];
    let initials = {};
    let rules = {};
    Promise.all(
      location.state.rowData.categories.map(async (cat) => {
        const res = await getAttributesService(cat.id);
        if (res.status == 200) {
          attrVar = [
            ...attrVar,
            { groupTitle: cat.title, data: res.data.data },
          ];
          if (res.data.data.length > 0) {
            for (const d of res.data.data) {
              initials = { ...initials, [d.id]: "" };
              rules = { ...rules, [d.id]: Yup.string() };
            }
          }
        }
      })
    ).then(() => {
      setTypeAtt(attrVar);
      setInitialValues(initials);
      setValidationSchema
    });
  };
  useEffect(() => {
    handleGetTypeOfAtt();
  }, []);

  useEffect(() => {
    console.log(typeAtt?.map((item) => item.data)[0]);
    setIsAnyAttr(typeAtt?.map((item) => item.data)[0]);
  }, [typeAtt]);

  //======================== üìç return =============================
  return (
    <div className=" container mt-5">
      {/* ================= üìç üëáheader of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container ">
        <span className="d-flex">
          <h5 className="fw-bold">ÿßŸÅÿ≤ŸàÿØŸÜ Ÿà€å⁄ò⁄Ø€å ÿ¨ÿØ€åÿØ :</h5>
          <h5 className="text-primary px-2">{location.state.rowData.title}</h5>
        </span>
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>
      {/* ================= üìç üëÜheader of page  ==================== */}
      <div className="d-flex justify-content-center">
        <div className="col-12 col-xxl-7 col-xl-8 col-lg-10">
          <Formik
            initialValues={initialValues}
            onSubmit={(value) => onSubmit(Id_product, value)}
            validationSchema={validationSchema}
          >
            {(formik) => {
              console.log(formik.values);
              return (
                <div className="">
                  {isSpinnerOn && !isAnyAttr?.length ? (
                    <GreenSpinner />
                  ) : isAnyAttr?.length ? (
                    <Form>
                      {typeAtt?.map((item, index) => (
                        <div className="py-2">
                          <div className="text-center py-2">
                            <span className="fs-4"> ⁄Øÿ±ŸàŸá:</span>
                            <span className="fs-5">{item.groupTitle} </span>
                          </div>
                          {item.data.map((d) => (
                            <FormikControl
                              control="typeOfAtt"
                              name={`typeOfAtt${d.id}`}
                              titleOfAttr={d.title}
                              unitOfAttr={d.unit}
                            />
                          ))}
                        </div>
                      ))}

                      <div className=" text-center pt-4">
                        <SubmitBtn />
                      </div>
                    </Form>
                  ) : (
                    <div className="text-center p-5 fs-5 fw-bolder text-primary">
                      Ÿá€å⁄Ü Ÿà€å⁄ò⁄Ø€å ÿß€å ÿ®ÿ±ÿß€å ÿß€åŸÜ ŸÖÿ≠ÿµŸàŸÑ ÿ´ÿ®ÿ™ ŸÜÿ¥ÿØŸá ÿßÿ≥ÿ™!...
                    </div>
                  )}
                </div>
              );
            }}
          </Formik>
        </div>
      </div>
    </div>
  );
};

