import React from "react";
import { PrevPageBtn } from "../../../general_compo/reusable_operations/PrevPageBtn";
import { Form, Formik } from "formik";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "../FormikHelper_products";
import { FormikControl } from "../../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../../formik/SubmitBtn";
import { useState } from "react";
import { useEffect } from "react";
import { getCategoriesService } from "../../../../services/CRUD_categoryService";
import { useLocation } from "react-router-dom";

export const AddProduct = () => {
  const [mainCategories, setMainCategories] = useState([]);
  //const [mainCategoryID, setMainCategoryID] = useState([]);
  const [reInitialize, setReInitialize] = useState([]);
  const location = useLocation();

  useEffect(() => {
    console.log(location);
   
  }, [location]);

  const handleGetCategories = async () => {
    const res = await getCategoriesService();
    if (res.status == 200) {
      //console.log(res.data.data);
      if (typeof res.data.data == "object") {
        setMainCategories([]);

        for (const key in res.data.data) {
          setMainCategories((oldData) => [
            ...oldData,
            { id: res.data.data[key].id, value: res.data.data[key].title },
          ]);
        }
      }
    }
  };

  useEffect(() => {
    handleGetCategories();
  }, []);

  return (
    <div className="">
      {/* ================= 📍 header of page  ==================== */}
      <div className="header_addProduct p-3 d-flex justify-content-between container">
        <h5>
          {location.stateAdd?.title_for_adding
            ? location.stateAdd?.title_for_adding
            : " location.state?.title_for_editing"}
        </h5>
        <PrevPageBtn
          customStyle="fs-2 text-start text-secondary"
          returnTitle={<i className="fa-solid fa-xmark"></i>}
        />
      </div>

      {/* =================== 📍 start form  ======================= */}
      <div className="container col-lg-5 ">
        <Formik
          initialValues={initialValues}
          onSubmit={(rowData) => onSubmit(rowData)}
          validationSchema={validationSchema}
        >
          {(form) => {
            console.log(value);
            return (
              <Form>
                {mainCategories.length ? (
                  <FormikControl
                    control="select"
                    label="دسته"
                    option={mainCategories}
                    name="category_ids"
                  />
                ) : (
                  "دسته والد"
                )}
                <FormikControl
                  control="input"
                  type="text"
                  name="title"
                  placeholder="عنوان"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="price"
                  title="قیمت"
                  placeholder="قیمت (تومان)"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="weight"
                  title="وزن"
                  placeholder="وزن (کیلوگرم)"
                />
                برند
                <FormikControl
                  control="input"
                  type="text"
                  name="brand_id"
                  placeholder="برند"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="color_ids"
                  placeholder="رنگ"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="guarantee_ids"
                  title="گارانتی"
                  placeholder="گارانتی (ماه/سال)"
                />
                <FormikControl
                  control="textArea"
                  type="type"
                  name="descriptions"
                  placeholder="توضیحات"
                />
                <FormikControl
                  control="addFile"
                  formik={form}
                  label="تصویر"
                  name="image"
                  placeholder="📂 برای انتخاب تصویر اینجا کلیک کنید"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="alt_image"
                  placeholder="توضیح تصویر"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="keywords"
                  placeholder=" تگ ها"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="stock"
                  title="موجود"
                  placeholder="موجودی (تعداد)"
                />
                <FormikControl
                  control="input"
                  type="text"
                  name="discount"
                  title="تخفیف"
                  placeholder="درصد تخفیف"
                />
                <div className=" text-center pt-4">
                  <SubmitBtn />
                </div>
              </Form>
            );
          }}
        </Formik>
      </div>
    </div>
  );
};
