import React, { useEffect, useState } from "react";
import { getProductsService } from "../../../../services/CRUD_categoryService";

export const Reusable_table2 = ({ nameOfColumn }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchChar, setSearchChar] = useState("");
  const [currentPage, setCurrentPage] = useState(1); // صفحه حال حاضر
  const [countOnPage, setCountOnPage] = useState(10); // تعداد محصول در هر صفحه
  const [pageCount, setPageCount] = useState(0); // تعداد کل صفحات

  const searchParams = {
    title: "جستجو",
    placeholder: "قسمتی از عنوان را وارد کنید",
  };

  const handleGetProducts = async (page, count, char) => {
    setLoading(true);
    const res = await getProductsService(page, count, char);
    res && setLoading(false);
    if (res.status === 200) {
      setData(res.data.data);
      setPageCount(res.data.last_page);
    }
  };

  const handleSearch = (char) => {
    setSearchChar(char);
    handleGetProducts(1, countOnPage, char);
  };

  useEffect(() => {
    handleGetProducts(currentPage, countOnPage, searchChar);
  }, [currentPage]);



  const pageRange = 3  

  const [pages, setPages] = useState([]);

  let timeout;

  const handleSetSearchChar = (char)=>{
    clearTimeout(timeout);
    timeout = setTimeout(()=>{
      handleSearch(char)
    },2000)
  }

  useEffect(()=>{
    let pArr = [];
    for (let i = 1; i <= pageCount; i++) pArr.push(i);
    setPages(pArr);
  },[pageCount])

  return (
    <div className="p-1 reusable_table">
      <table className=" table_of_reusable_table table table-striped table-responsive text-center table-hover table-bordered">
        <thead>
          <tr>
            {nameOfColumn.map((item) => (
              <th>{item.title}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((data) => (
            <tr>
              {nameOfColumn.map((item) => (
                <td>{data[item.field]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
