import React, { useState } from "react";
import { AddItem_btn } from "../../general_compo/add_or_edit_product/AddItem_btn";
import { Form, Formik } from "formik";
import { FormikControl } from "../../formik/FormikControl_AddItems";
import { SubmitBtn } from "../../formik/SubmitBtn";
import {
  initialValues,
  onSubmit,
  validationSchema,
} from "./FormikHelper_discounts";

export const AddNewDiscounts_modal = () => {
  const [selectedProducts, setSelectedProducts] = useState([]);

  const handleSetProductSelectBox = (formik) => {
    const idsArr = formik.values.product_ids.split("-").filter((id) => id);
    const selectedProductArr = idsArr.map(
      (id) => allProducts.filter((p) => p.id == id)[0]
    );
    console.log(selectedProductArr);
    return (
      <FormikControl
        className="animate__animated animate__shakeX"
        label="برای"
        control="searchableSelect"
        options={allProducts}
        name="product_ids"
        firstItem="محصول مورد نظر را انتخاب کنبد..."
        resultType="string"
        initialItems={
          selectedProductArr.length > 0 ? selectedProductArr : selectedProducts
        }
      />
    );
  };

  return (
    <div>
      {/* <!-- Button trigger modal --> */}
      <span data-bs-toggle="modal" data-bs-target="#staticBackdrop">
        <AddItem_btn />
      </span>

      {/* <!-- Modal --> */}
      <div
        className="modal fade"
        id="staticBackdrop"
        data-bs-backdrop="static"
        data-bs-keyboard="false"
        tabindex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header d-flex justify-content-between align-items-center">
              <h5 className="modal-title w-100 mx-5" id="staticBackdropLabel">
                افزودن کد تخفیف
              </h5>
              <button
                type="button"
                className="btn-close w-25 me-5"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>

            <div className="modal-body">
              <Formik
                initialValues={initialValues}
                onSubmit={(values) => onSubmit(values)}
                validationSchema={validationSchema}
              >
                {(formik) => {
                  console.log(formik.values);
                  return (
                    <Form>
                      <FormikControl
                        control="input"
                        type="text"
                        name="title"
                        placeholder="عنوان"
                      />
                      <FormikControl
                        control="input"
                        type="text"
                        name="code"
                        placeholder="کد تخفیف"
                      />
                      <FormikControl
                        control="input"
                        type="number"
                        name="percent"
                        placeholder="درصد تخفیف"
                      />

                      <FormikControl
                        control="persianDate"
                        formik={formik}
                        name="expire_at"
                        label="تاریخ انقضاء"
                        yearsLimit={{ from: 10, to: 10 }}
                      />

                      <FormikControl
                        control="checkbox"
                        name="for_all"
                        label="برای همه"
                        customClass="text-center"
                      />

                      {!formik.values.for_all
                        ? handleSetProductSelectBox(formik)
                        : null}
                      <div className="text-center">
                        <SubmitBtn />
                      </div>
                    </Form>
                  );
                }}
              </Formik>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
