import { ErrorMessage, FastField ,Field } from "formik";
import React from "react";

export const TextArea = (props) => {
  const {type, name, placeholder, categoryDescriptions } = props;
  return (
    <div className="p-2">
      <div className="input-group  ">
        <span className="input-group-text titleSpan">{placeholder}</span>
        {/* <textarea
          name={name}
          placeholder={placeholder}
          className="form-control"
          aria-label="With textarea"
        ></textarea> */}
        <FastField
          type={type}
          name={name}
          className="form-control"
          placeholder={placeholder}
        />
        <Field
            as='textarea'
            name='comments'
            id='comments'
            cols='30'
            rows='10'
            // placeholder='comments'
            className='bg-gray-100 p-2 px-4 placeholder-black w-4/12  mb-6'
          />
        <ErrorMessage
          name={name}
          render={(err) => <small className="text-danger">{err}</small>}
        />
      </div>
    </div>
  );
};
